import { useState } from "react";

const Jayatable = () => {
  const [page, setPage] = useState(1);

  const data = [
    ["1", "বিনামূল্যে", "৳300"],
    ["2", "৳20", "৳800"],
    ["3", "বিনামূল্যে", "৳1300"],
    ["4", "৳10", "৳1800"],
    ["5", "বিনামূল্যে", "৳2300"],
    ["6", "৳10", "৳3100"],
    ["7", "বিনামূল্যে", "৳3900"],
    ["8", "৳15", "৳4700"],
    ["9", "বিনামূল্যে", "৳5500"],
    ["10", "৳50", "৳6300"],
  ];

  const tableData = page === 1 ? data.slice(0, 3) : data.slice(3, 6);

  return (
    <div className="p-4">
      <table className="w-full border border-gray-300">
        <thead>
          <tr className="bg-gray-200">
            <th className="border p-2">স্তর</th>
            <th className="border p-2">পুরস্কার</th>
            <th className="border p-2">প্রয়োজনীয়তা</th>
          </tr>
        </thead>
        <tbody>
          {tableData.map((row, index) => (
            <tr key={index} className="text-center">
              {row.map((cell, i) => (
                <td key={i} className="border p-2">{cell}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      <div className="flex justify-center mt-4">
        <button
          onClick={() => setPage(1)}
          className={`px-4 py-2 mx-2 border ${
            page === 1 ? "bg-blue-500 text-white" : "bg-gray-200"
          }`}
        >
          Page 1
        </button>
        <button
          onClick={() => setPage(2)}
          className={`px-4 py-2 mx-2 border ${
            page === 2 ? "bg-blue-500 text-white" : "bg-gray-200"
          }`}
        >
          Page 2
        </button>
      </div>
    </div>
  );
};

export default Jayatable;
