import React, { useState } from "react";

// Modal কম্পোনেন্ট
const Modal = ({ isOpen, handleClose, handleSubmit, handleChange, formData }) => {
  if (!isOpen) return null; // If modal is not open, don't render

  return (
    <div
      className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50"
      onClick={handleClose} // Outside click to close modal
    >
      <div
        className="bg-white rounded-md shadow-md w-80"
        onClick={(e) => e.stopPropagation()} // Prevent closing when clicking inside modal
      >
        {/* Close Button (Using SVG) */}
        <div className="flex flex-row px-5 py-3 items-center justify-between bg-common-blue">
          <div>
            <h2 className="font-semibold text-white">ব্র্যান্ড] এ স্বাগতম</h2>
          </div>
          <div>
            <button onClick={handleClose} className="text-gray-600 hover:text-red-500">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="w-6 h-6"
                viewBox="0 0 24 24"
                fill="none"
                stroke="white"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
              >
                <line x1="18" y1="6" x2="6" y2="18" />
                <line x1="6" y1="6" x2="18" y2="18" />
              </svg>
            </button>
          </div>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="space-y-3">
          <div className="px-5 py-8">
            <div>
              <h3 className="text-black">ব্যবহারকারীর নাম</h3>
              <input
                type="text"
                name="ব্যবহারকারীর নাম"
                placeholder="এখানে পূরণ করুন"
                className="w-full p-2 text-black border rounded-md focus:outline-none focus:ring-1 focus:ring-red-500"
                onChange={handleChange}
                value={formData["ব্যবহারকারীর নাম"]}
              />
            </div>
            <div>
              <h3 className="text-black">গোপন নম্বর</h3>
              <input
                type="password"
                name="password"
                placeholder="এখানে পূরণ করুন"
                className="w-full p-2 border text-black rounded-md focus:outline-none focus:ring-1 focus:ring-red-500"
                onChange={handleChange}
                value={formData.password}
              />
              <div className="flex justify-end">
                <p className="my-2 text-end text-black text-sm cursor-pointer hover:text-common-blue hover:border-b hover:border-common-blue w-fit whitespace-nowrap">
                  পাসওয়ার্ড ভুলে গেছেন?
                </p>
              </div>
            </div>
            <div>
              <button
                type="submit"
                className="w-full bg-orange-500 text-white py-2 rounded-md hover:bg-orange-600"
              >
                প্রবেশ করুন
              </button>

              <p className="mt-2 text-black text-sm cursor-pointer w-fit">
                অ্যাকাউন্ট নেই? <a href="/register">নিবন্ধন</a>
              </p>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

const ProbesKorun = () => {
  const [isOpenModal, setIsOpenModal] = useState(false);
  const [formData, setFormData] = useState({
    "ব্যবহারকারীর নাম": "",
    password: "",
  });

  const handleOpen = () => {
    setIsOpenModal(true);
  };

  const handleClose = () => {
    setIsOpenModal(false);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("User Data:", formData);
  };

  return (
    <div className="flex items-center justify-center">
      {/* প্রবেশ করুন Button */}
      

      {/* Modal */}
      <Modal
        isOpen={isOpenModal}
        handleClose={handleClose}
        handleSubmit={handleSubmit}
        handleChange={handleChange}
        formData={formData}
      />
    </div>
  );
};

export default ProbesKorun;
