import { useState, useEffect } from "react";
import image1 from '../../assets/image1.jpg'
import image2 from '../../assets/image2.jpg'
import image3 from '../../assets/image3.jpg'
import image4 from '../../assets/image4.jpg'
import image5 from '../../assets/image5.jpg'
import image6 from '../../assets/image6.jpg'
import image7 from '../../assets/image7.jpg'

const tabData = [
  { id: 1, label: "Tab 1", data: [
      { image: image1, title: "BPL Giveaway", description: "দৈনিক গিভঅ্যাওয়ে BPL ২০২৪-২০২৫", countdown: "6 D 1 H", details: true, apply: true },
      { image: "/images/promo2.jpg", title: "Exclusive Offer", description: "প্রতিদিন ৯৯ জন সদস্যের জন্য", countdown: "3 D 5 H", details: true, apply: false }
    ] },
  { id: 2, label: "Tab 2", data: [
      { image: "/images/promo3.jpg", title: "Special Discount", description: "প্রতিদিন সীমিত অফার", countdown: "2 D 8 H", details: true, apply: true },
      { image: "/images/promo4.jpg", title: "Mega Giveaway", description: "এক্সক্লুসিভ পুরস্কার", countdown: "7 D 12 H", details: false, apply: true }
    ] },
  { id: 3, label: "Tab 3", data: [
      { image: "/images/promo5.jpg", title: "Limited Edition", description: "বিশেষ সংস্করণ অফার", countdown: "5 D 3 H", details: true, apply: false }
    ] },
  { id: 4, label: "Tab 4", data: [
      { image: "/images/promo6.jpg", title: "Bonus Reward", description: "নতুন সদস্যদের জন্য", countdown: "4 D 6 H", details: false, apply: true }
    ] },
  { id: 5, label: "Tab 5", data: [] },
  { id: 6, label: "Tab 6", data: [] },
  { id: 7, label: "Tab 7", data: [] },
  { id: 8, label: "Tab 8", data: [] }
];

const Promotion = () => {
  const [activeTab, setActiveTab] = useState(1);
  const [promotions, setPromotions] = useState([]);

  useEffect(() => {
    const selectedTab = tabData.find((tab) => tab.id === activeTab);
    setPromotions(selectedTab?.data || []);
  }, [activeTab]);

  return (
    <div className="w-full p-4">
      {/* Tabs */}
      <div className="flex gap-4 border-b pb-2 mb-4">
        {tabData.map((tab) => (
          <button
            key={tab.id}
            className={`px-4 py-2 text-sm font-semibold rounded-t-lg transition-all ${
              activeTab === tab.id ? "bg-blue-600 text-white" : "bg-gray-200"
            }`}
            onClick={() => setActiveTab(tab.id)}
          >
            {tab.label}
          </button>
        ))}
      </div>

      {/* Cards */}
      <div className="grid grid-cols-2 gap-4">
        {promotions.length > 0 ? (
          promotions.map((item, index) => (
            <div key={index} className="relative bg- p-4 shadow-lg rounded-lg">
              <img src={item.image} alt="promotion" className="w-full h-40 object-cover rounded-md" />
              <h2 className="mt-2 text-lg font-bold">{item.title}</h2>
              <p className="text-sm text-gray-600">{item.description}</p>
              <div className="absolute top-2 right-2 bg-red-500 text-white px-2 py-1 text-xs rounded">
                {item.countdown}
              </div>
              <div className="mt-4 flex gap-2">
                {item.details && <button className="bg-blue-500 text-white px-3 py-1 rounded">বিস্তারিত</button>}
                {item.apply && <button className="bg-green-500 text-white px-3 py-1 rounded">আবেদন</button>}
              </div>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-600">অনুগ্রহ করে সাথেই থাকুন. নতুন প্রচার আপনার পথে আসছে।</p>
        )}
      </div>
    </div>
  );
};

export default Promotion;
