import { useState } from "react";

const CoinIcon = () => (
  <svg width="16" height="16" viewBox="0 0 24 24" fill="gold" xmlns="http://www.w3.org/2000/svg">
    <circle cx="12" cy="12" r="10" stroke="black" strokeWidth="2" fill="gold" />
  </svg>
);

const Jayatable = () => {
  const [page, setPage] = useState(1);
  const itemsPerPage = 10;

  const levels = Array.from({ length: 20 }, (_, i) => (i + 1).toString());
  const rewards = levels.map((_, i) => (
    <div className="flex flex-col items-center">
      <CoinIcon />
      <span>বিনামূল্যে</span>
      <span>৳{(i + 1) * 10}</span>
    </div>
  ));
  const requirements = levels.map((_, i) => `৳${(i + 1) * 500}`);

  const totalPages = Math.ceil(levels.length / itemsPerPage);
  const startIndex = (page - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;

  return (
    <div className="p-4">
      <table className="w-full border border-gray-300">
        <tbody>
          <tr className="text-center bg-gray-200">
            <td className="border p-2">স্তর</td>
            {levels.slice(startIndex, endIndex).map((level, i) => (
              <td key={i} className="border p-2 bg-gray-400 text-white font-bold rounded-md">{level}</td>
            ))}
          </tr>
          <tr className="text-center bg-gray-100">
            <td className="border p-2">পুরস্কার</td>
            {rewards.slice(startIndex, endIndex).map((reward, i) => (
              <td key={i} className="border p-2 flex flex-col items-center">{reward}</td>
            ))}
          </tr>
          <tr className="text-center bg-gray-200">
            <td className="border p-2">প্রয়োজনীয়তা</td>
            {requirements.slice(startIndex, endIndex).map((requirement, i) => (
              <td key={i} className="border p-2">{requirement}</td>
            ))}
          </tr>
        </tbody>
      </table>
      <div className="flex justify-center mt-4">
        <button
          onClick={() => setPage(page - 1)}
          disabled={page === 1}
          className="px-4 py-2 mx-2 border bg-gray-200 disabled:opacity-50"
        >
          Previous
        </button>
        <span className="px-4 py-2">
          {Array.from({ length: totalPages }, (_, i) => (
            <button
              key={i + 1}
              className={`px-3 py-1 mx-1 border ${page === i + 1 ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
              onClick={() => setPage(i + 1)}
            >
              {i + 1}
            </button>
          ))}
        </span>
        <button
          onClick={() => setPage(page + 1)}
          disabled={page === totalPages}
          className="px-4 py-2 mx-2 border bg-gray-200 disabled:opacity-50"
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default Jayatable;
