import React, { useState } from 'react';

const About = () => {
  const [activeTab, setActiveTab] = useState('tab1'); // প্রথম ট্যাব ডিফল্ট হিসেবে দেখাবে

  return (
    <section className="p-8">
      <h2 className="text-xl font-semibold mb-4">আমাদের সম্পর্কে</h2>
      <div className="flex space-x-4 mb-4">
        {/* ট্যাব ১ */}
        <button
          className={`py-2 px-4 rounded-lg transition duration-300 ${
            activeTab === 'tab1' ? 'bg-blue-500 text-white' : 'bg-gray-200'
          }`}
          onClick={() => setActiveTab('tab1')}
        >
          ট্যাব ১
        </button>

        {/* ট্যাব ২ */}
        <button
          className={`py-2 px-4 rounded-lg transition duration-300 ${
            activeTab === 'tab2' ? 'bg-blue-500 text-white' : 'bg-gray-200'
          }`}
          onClick={() => setActiveTab('tab2')}
        >
          ট্যাব ২
        </button>

        {/* ট্যাব ৩ */}
        <button
          className={`py-2 px-4 rounded-lg transition duration-300 ${
            activeTab === 'tab3' ? 'bg-blue-500 text-white' : 'bg-gray-200'
          }`}
          onClick={() => setActiveTab('tab3')}
        >
          ট্যাব ৩
        </button>
      </div>

      {/* ট্যাবের বিষয়বস্তু */}
      <div className="tab-content mt-4">
        {activeTab === 'tab1' && (
          <div>
            <img
              src="https://via.placeholder.com/150"
              alt="Tab 1 Example"
              className="mb-4"
            />
            <p>এটি ট্যাব ১ এর তথ্য। এখানে আপনি প্রথম বিষয় সম্পর্কে বিস্তারিত জানতে পারবেন।</p>
          </div>
        )}
        {activeTab === 'tab2' && (
          <div>
            <img
              src="https://via.placeholder.com/150"
              alt="Tab 2 Example"
              className="mb-4"
            />
            <p>এটি ট্যাব ২ এর তথ্য। এখানে দ্বিতীয় বিষয় সম্পর্কে তথ্য দেওয়া হয়েছে।</p>
          </div>
        )}
        {activeTab === 'tab3' && (
          <div>
            <img
              src="https://via.placeholder.com/150"
              alt="Tab 3 Example"
              className="mb-4"
            />
            <p>এটি ট্যাব ৩ এর তথ্য। এখানে তৃতীয় বিষয়ের বিবরণ রয়েছে।</p>
          </div>
        )}
      </div>
    </section>
  );
};

export default About;
