import { useState, useEffect } from "react";
import coinEventslogo from "../../assets/coin-event.svg";
import coinUnchecklogo from "../../assets/coin_unchecked.svg";
import coinlogo from "../../assets/coin.svg";

const AutoOpenModal = () => {
  const [isOpen, setIsOpen] = useState(false);
  const images = [
    { src: "coinUnchecklogo",number:'1', day: "Mon", count: 4 },
    { src: "coinUnchecklogo",number:'2', day: "Mon", count: 4 },
    { src: "coinUnchecklogo",number:'1', day: "Mon", count: 4 },
    { src: "coinUnchecklogo",number:'1', day: "Mon", count: 4 },
    { src: "coinlogo", text: "Image 2: Description", count: 3 },
  ];

  useEffect(() => {
    setIsOpen(true); // Page load hole modal open
  }, []);

  const closeModal = () => {
    setIsOpen(false);
  };

  return (
    <div>
      {/* Modal Overlay (Outside Click Close) */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center"
          onClick={closeModal} // Outside click e close
        >
          {/* Modal Content */}
          <div
            className="bg-white p-5 rounded-lg shadow-lg relative w-96"
            onClick={(e) => e.stopPropagation()} // Modal content e click hole close hobe na
          >
            <h2 className="text-lg py-2 border-b border-gray-400 font-bold mb-2">
              দৈনিক চেক ইন
            </h2>
            <button
              className="absolute top-7 right-2 text-xl"
              onClick={closeModal} // Cross button e close
            >
              ✖
            </button>
            <div className="flex flex-row items-center justify-center bg-gray-300 py-1">
              <img src={coinEventslogo} alt="" className="" />
              <h3 className="text-sm">18</h3>
            </div>
            <div className="grid grid-cols-7 gap-4">
              {images.map((image, index) =>
                // Map through images array
                Array.from({ length: image.count }).map((_, i) => (
                  <div key={`${index}-${i}`} className="image-container">
                    <img
                      src={image.src}
                      alt={` ${index + 1}`}
                      className="w-4 h-auto"
                    />
                    <p className="text-center">{image.text}</p>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AutoOpenModal;
