import React, { useState } from 'react';

const Baji = () => {
  const tabs = ['Overview', 'ক্রিকেট', 'লাইভ ক্যাসিনো', 'স্লট গেম', 'টেবিল গেম', 'স্পোর্টস', 'মাছ ধরা'];
  const [activeTab, setActiveTab] = useState('Overview');
  
  const overviewColumns = [
    "তারিখ",
    "Bet Count",
    "Bet Amount",
    "জয় পরাজয়",
    "Jackpot Contribution",
    "Jackpot Amount",
  ];
  
  const commonColumns = [
    "Column 1",
    "Column 2",
    "Column 3",
    "Column 4",
    "Column 5",
    "Column 6",
  ];

  return (
    <div className=" bg-informationBackground  min-h-screen">
     <div className='mx-auto pt-4 bg-white max-w-6xl min-h-screen'>
         {/* Tab buttons */}
      <div className="flex overflow-x-auto whitespace-nowrap text-xs space-x-2 items-center lg:justify-center mb-4" role="tablist">
        {tabs.map((tab, index) => (
          <button
            key={index}
            role="tab"
            aria-selected={activeTab === tab}
            className={`px-4 py-2 rounded transition-all duration-300 ease-in-out ${
              activeTab === tab ? "bg-common-blue text-white" : "bg-gray-200"
            }`}
            onClick={() => setActiveTab(tab)}
          >
            {tab === 'Overview' && (
                <div className="flex items-center">
                  {/* Custom SVG Logo */}
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="20"
                    height="20"
                    viewBox="0 0 24 24"
                    fill="none"
                    stroke="#f56908"
                    strokeWidth="2"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    className="inline-block mr-2 md:hidden"
                  >
                    <path
                      d="M12 2L2 12h3v6h14v-6h3L12 2z"
                    />
                  </svg>
                  <span>{tab}</span>
                </div>
              )}
              {tab !== 'Overview' && tab}
          </button>
        ))}
      </div>

      {/* Table */}
      <div className="overflow-x-auto">
        <table className="w-full ">
          <thead className='hidden lg:flex jus'>
            <tr className="border-b border-gray-400">
              {(activeTab === "Overview" ? overviewColumns : commonColumns).map((col, index) => (
                <th key={index} className=" p-2 text-left">
                  {col}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {/* Placeholder row for no data */}
            <tr className=''>
              <td colSpan={activeTab === "Overview" ? overviewColumns.length : commonColumns.length} className="text-center  text-pretty  text-gray-400 ">
                কোন তথ্য নেই
              </td>
            </tr>
          </tbody>
        </table>
      </div>
     </div>
    </div>
  );
};

export default Baji;
