import React from "react";
import certificatiologo from "../assets/gaming_Casino_Analyzer.svg";
import certification2 from "../assets/certificatio2.svg";
import gaminglogo from "../assets/gaming.webp";
import jayalogo from "../assets/logo3.png";
import { useLocation } from "react-router-dom";
import CricketBattingFooter from "./CricketBattingFooter/CricketBattingFooter";



const Footer = () => {
    const location = useLocation();

  const footerComponents = {
    "/cricket": <CricketBattingFooter />
  };
  return (
    <div className="bg-gray- mt-3 py-3">
      <div className="flex flex-row justify-around font-medium   font-serif  ">
        <div>
          <h3>মুল্য পরিশোধ পদ্ধতি</h3>
        </div>
        <div>
          <h3>গ্রাহক সমর্থন</h3>
        </div>
        <div>
          <h3>সার্টিফিকেশন</h3>
          <div className="flex flex-row gap-2">
            <img src={certificatiologo} alt="" />
            <img src={certification2} alt="" />
          </div>
        </div>
        <div>
          <h3>কমিউনিটি ওয়েবসাইট</h3>
        </div>
        <div>
          <h3>গেমিং লাইসেন্স</h3>
          <img src={gaminglogo} alt="" className=" h-8" />
        </div>
      </div>
      <div className="  py-4 text-center">
      {footerComponents[location.pathname] || null}
    </div>
      <div className="flex flex-row px-8 pt-4 justify-between">
        <div>
          <img src={jayalogo} alt="" className="h-12" />
        </div>
        <div className="space-y-3 pb-6" >
          <p className="text-blue-900 gap-2 text-sm w-" ><span className="border-b border-blue-900 pr-6">দায়িত্বশীল গেমিং</span> <span className="border-b border-blue-900 pl-3 pr-4">শর্তাবলী</span> <span className="border-b border-blue-900">আমাদের সম্পর্কে</span></p>
          <p className="text-gray-600 font-bold text-xs">কপিরাইট © 2025 [ ব্র্যান্ড]। সমস্ত অধিকার সংরক্ষিত</p>
        </div>
      </div>
    </div>
  );
};

export default Footer;
