import { useState } from "react";

const Jayatable = () => {
  const [page, setPage] = useState(1);
  const itemsPerPage = 3;

  const data = [
    ["1", "বিনামূল্যে", "৳300", "৳400", "৳500", "৳600", "৳700", "৳800", "৳900", "৳1000", "৳1100"],
    ["2", "৳20", "৳800", "৳900", "৳1000", "৳1100", "৳1200", "৳1300", "৳1400", "৳1500", "৳1600"],
    ["3", "বিনামূল্যে", "৳1300", "৳1400", "৳1500", "৳1600", "৳1700", "৳1800", "৳1900", "৳2000", "৳2100"],
    ["4", "৳10", "৳1800", "৳1900", "৳2000", "৳2100", "৳2200", "৳2300", "৳2400", "৳2500", "৳2600"],
    ["5", "বিনামূল্যে", "৳2300", "৳2400", "৳2500", "৳2600", "৳2700", "৳2800", "৳2900", "৳3000", "৳3100"],
    ["6", "৳10", "৳3100", "৳3200", "৳3300", "৳3400", "৳3500", "৳3600", "৳3700", "৳3800", "৳3900"],
    ["7", "বিনামূল্যে", "৳3900", "৳4000", "৳4100", "৳4200", "৳4300", "৳4400", "৳4500", "৳4600", "৳4700"],
    ["8", "৳15", "৳4700", "৳4800", "৳4900", "৳5000", "৳5100", "৳5200", "৳5300", "৳5400", "৳5500"],
    ["9", "বিনামূল্যে", "৳5500", "৳5600", "৳5700", "৳5800", "৳5900", "৳6000", "৳6100", "৳6200", "৳6300"],
    ["10", "৳50", "৳6300", "৳6400", "৳6500", "৳6600", "৳6700", "৳6800", "৳6900", "৳7000", "৳7100"],
  ];

  const totalPages = Math.ceil(data.length / itemsPerPage);
  const tableData = data.slice((page - 1) * itemsPerPage, page * itemsPerPage);

  return (
    <div className="p-4">
      <table className="w-full border border-gray-300">
        <thead>
          <tr className="bg-gray-200">
            <th className="border p-2">স্তর</th>
            <th className="border p-2">পুরস্কার</th>
            {[...Array(9)].map((_, i) => (
              <th key={i} className="border p-2">প্রয়োজনীয়তা {i + 1}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {tableData.map((row, index) => (
            <tr key={index} className="text-center">
              {row.map((cell, i) => (
                <td key={i} className="border p-2">{cell}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      <div className="flex justify-center mt-4">
        <button
          onClick={() => setPage(page - 1)}
          disabled={page === 1}
          className="px-4 py-2 mx-2 border bg-gray-200 disabled:opacity-50"
        >
          Previous
        </button>
        <span className="px-4 py-2">Page {page} of {totalPages}</span>
        <button
          onClick={() => setPage(page + 1)}
          disabled={page === totalPages}
          className="px-4 py-2 mx-2 border bg-gray-200 disabled:opacity-50"
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default Jayatable;
