import React from "react";
import { FaGlobe } from "react-icons/fa";
import { useEffect, useState } from "react";
import image1 from "../../assets/image1.jpg";
import image2 from "../../assets/image2.jpg";
import image3 from "../../assets/image3.jpg";
import image4 from "../../assets/image4.jpg";
import image5 from "../../assets/image5.jpg";
import image6 from "../../assets/image6.jpg";
import image7 from "../../assets/image7.jpg";
import banner from "../../assets/home banner.jpg";

const bgImage = "https://via.placeholder.com/400"; // Same background image for all

const data = [
  { id: 1, title: "Title One", description: "This is a short description.", extraInfo1: "Extra info 1", extraInfo2: "Extra info 2" },
  { id: 2, title: "Title Two", description: "Another short description.", extraInfo1: "More info 1", extraInfo2: "More info 2" },
  { id: 3, title: "Title Three", description: "Yet another description.", extraInfo1: "Some info", extraInfo2: "Other info" },
  { id: 4, title: "Title Four", description: "Something interesting.", extraInfo1: "Detail 1", extraInfo2: "Detail 2" },
  { id: 5, title: "Title Five", description: "Short but effective.", extraInfo1: "Info X", extraInfo2: "Info Y" },
  { id: 6, title: "Title Six", description: "Final card description.", extraInfo1: "End info", extraInfo2: "Last info" }
];
  

const Home = () => {
  const images = [image1, image2, image3, image4, image5, image6, image7];
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000); // 3 seconds interval

    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <div>
      <div className="w-full flex flex-col items-center">
        {/* Image Container */}
        <div className="relative w-full lg:grid lg:grid-cols-1 overflow-hidden">
          <img
            src={images[currentIndex]}
            alt=""
            className="w-full h-64 object-cover transition-opacity 
          
          "
          />

          {/* Navigation Buttons Inside Image */}
          <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex gap-2 bg-black bg-opacity-50 px-4 py-2 rounded-lg">
            {images.map((_, index) => (
              <button
                key={index}
                onClick={() => setCurrentIndex(index)}
                className={`w-3 h-3 rounded-full ${
                  currentIndex === index ? "bg-common-orange" : "bg-white"
                }`}
              ></button>
            ))}
          </div>
        </div>
      </div>

      <div className="w-full overflow-hidden bg-common-blue px-8 py-2 flex items-center">
        <div className="flex items-center space-x-4  text-white overflow-hidden">
          <div className="text-common-orange text-2xl ">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 24 24"
              width="32"
              height="32"
              fill="currentColor"
            >
              <path d="M12 14c1.66 0 3-1.34 3-3V5c0-1.66-1.34-3-3-3S9 3.34 9 5v6c0 1.66 1.34 3 3 3zm6.29-3c-.52 0-1.02-.2-1.41-.59-.78-.78-.78-2.05 0-2.83.39-.39.89-.59 1.41-.59s1.02.2 1.41.59c.78.78.78 2.05 0 2.83-.39.39-.89.59-1.41.59zM12 16c-2.76 0-5-2.24-5-5H5c0 3.53 2.61 6.43 6 6.92V21h2v-3.08c3.39-.49 6-3.39 6-6.92h-2c0 2.76-2.24 5-5 5zm5.3-4.71c-.39-.39-.39-1.02 0-1.41 1.17-1.17 1.17-3.07 0-4.24-.39-.39-.39-1.02 0-1.41.39-.39 1.02-.39 1.41 0 1.95 1.95 1.95 5.12 0 7.07-.39.39-1.02.39-1.41-.01z" />
            </svg>
          </div>
          <div className="w-full ">
            <marquee className="text-white block w-full">
              <span className="mx-4">
                WWW.JAYA9.APP এখন আমাদের অফিসিয়াল ওয়েবসাইট:
              </span>
              <span className="mx-4">
                WWW.JAYA9.APP আপনার বিশ্বস্ত প্ল্যাটফর্ম JAYA9
              </span>
            </marquee>
          </div>
        </div>
      </div>
      <div className="px-8">
        <h3>রেফারেল প্রোগ্রাম</h3>
        <img src={banner} alt="" className="w-full" />
      </div>
      <div className="px-8">
        <div>
          <div className="flex space-x-2 flex-row">
            <h3 className="font-bold">Live</h3>
            <button className="bg-green-500 px-6">Now</button>
          </div>
          <div>
          <div className="overflow-x-auto flex gap-6 p-6">
      {data.map((item) => (
        <div
          key={item.id}
          className="relative bg-cover bg-center text-white p-6 rounded-xl shadow-lg"
          style={{ backgroundImage: `url(${bgImage})` }}
        >
          <div className="bg-black bg-opacity-50 p-4 rounded-lg">
            <h3 className="text-xl font-bold">{item.title}</h3>
            <p className="mb-2">{item.description}</p>
            <div className="flex justify-between text-sm opacity-80">
              <p>{item.extraInfo1}</p>
              <p>{item.extraInfo2}</p>
            </div>
          </div>
        </div>
      ))}
    </div>
          </div>
        </div>
        
      </div>
    </div>
  );
};

export default Home;
