import React, { useState } from 'react';
import infologo from '../../assets/personal-info.jpg';
import memberlogo from '../../assets/memberpic.svg';

const Jachaikoron = () => {
    const [formData, setFormData] = useState({
        username: "",
        mobile: "",
        otp: "",
        email: "",
        dob: "",
        currency: "BDT",
      });
    
      const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
      };
      const handleSubmit = (e) => {
        e.preventDefault();
        console.log("Form Data:", formData);
      };
    return (
        <div className='mx-auto max-w-3xl min-h-screen mt-12 '>
           <div className='relative'>
            <img src={infologo} alt="" className='max-h-20 w-full md:max-h-32 rounded-2xl lg:max-h-32 ' />
            <img src={memberlogo} alt="" className='w-20 h-auto absolute top-20 left-1/2 transform -translate-x-1/2' />
           </div>
           <form onSubmit={handleSubmit} className="mt- p-4 space-y-4 bg-gray-100 rounded-lg">
      <div>
        <label className="block font-medium">ব্যবহারকারীর নাম</label>
        <input
          type="text"
          name="username"
          value={formData.username}
          onChange={handleChange}
          className="w-full p-2 border rounded"
        />
      </div>

      <div>
        <label className="block font-medium">মোবাইল নম্বর</label>
        <input
          type="tel"
          name="mobile"
          value={formData.mobile}
          onChange={handleChange}
          className="w-full p-2 border rounded"
        />
      </div>

      <div>
        <label className="block font-medium">Verify OTP</label>
        <input
          type="text"
          name="otp"
          value={formData.otp}
          onChange={handleChange}
          className="w-full p-2 border rounded"
        />
      </div>

      <div>
        <label className="block font-medium">ইমেইল</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          className="w-full p-2 border rounded"
        />
      </div>

      <div>
        <label className="block font-medium">জন্ম তারিখ</label>
        <input
          type="date"
          name="dob"
          value={formData.dob}
          onChange={handleChange}
          className="w-full p-2 border rounded"
        />
      </div>

      <div>
        <label className="block font-medium">মুদ্রা</label>
        <select
          name="currency"
          value={formData.currency}
          onChange={handleChange}
          className="w-full p-2 border rounded"
        >
          <option value="BDT">BDT</option>
          <option value="USD">USD</option>
          <option value="EUR">EUR</option>
        </select>
      </div>

      
    </form>
        </div>
    );
};

export default Jachaikoron;
