import React from 'react';

const Profile = ({ username }) => {
    // প্রথম অক্ষরটি ক্যাপিটাল করা
    const firstLetter = username ? username.charAt(0).toUpperCase() : "";
    console.log("Username:", username);

    return (
        <div className='mx-auto max-w-2xl flex items-center gap-4 p-4'>
            {/* Round Shape with First Letter */}
            <div className='flex items-center justify-start gap-4 p-4'>
                <div className='w-16 h-16 bg-black text-common-orange flex items-center justify-center text-3xl font-bold rounded-full'>
                    {firstLetter}
                </div>
                {/* Full Username */}
                <span className='text-white text-xl font-semibold'>
                    {username}
                </span>
            </div>
        </div>
    );
};

export default Profile;
