import React, { useState } from "react";
import livecasinoimage from "../../assets/livecasiono.jpg";
import betswizz from "../../assets/Betswiz.jpeg";

const images = [
  { src: betswizz, alt: "Betswiz Image 1" },
  { src: betswizz, alt: "Betswiz Image 2" },
  { src: betswizz, alt: "Betswiz Image 3" },
  { src: betswizz, alt: "Betswiz Image 4" },
  { src: betswizz, alt: "Betswiz Image 5" },
  { src: betswizz, alt: "Betswiz Image 6" },
  { src: betswizz, alt: "Betswiz Image 7" },
  { src: betswizz, alt: "Betswiz Image 8" },
  { src: betswizz, alt: "Betswiz Image 9" },
  { src: betswizz, alt: "Betswiz Image 10" },
  { src: betswizz, alt: "Betswiz Image 11" },
  { src: betswizz, alt: "Betswiz Image 12" },
  { src: betswizz, alt: "Betswiz Image 13" },
  { src: betswizz, alt: "Betswiz Image 14" },
  { src: betswizz, alt: "Betswiz Image 15" },
  { src: betswizz, alt: "Betswiz Image 16" },
  { src: betswizz, alt: "Betswiz Image 17" },
  { src: betswizz, alt: "Betswiz Image 18" },
  { src: betswizz, alt: "Betswiz Image 19" },
  { src: livecasinoimage, alt: "Betswiz Image 20" },
];
const itemsPerPage = 10; // Number of images per page
const totalPages = Math.ceil(images.length / itemsPerPage);

const LiveCasino = () => {
  const [currentPage, setCurrentPage] = useState(1);

  // Get images for the current page
  const indexOfLastImage = currentPage * itemsPerPage;
  const indexOfFirstImage = indexOfLastImage - itemsPerPage;
  const currentImages = images.slice(indexOfFirstImage, indexOfLastImage);

  const handlePageChange = (page) => {
    if (page >= 1 && page <= totalPages) {
      setCurrentPage(page);
    }
  };

  return (
    <div>
     <div>
     <img src={livecasinoimage} alt="Live Casino" cl />
     </div>

      <div className="flex flex-row items-center px-4 pt-4">
        <div>
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" viewBox="0 0 24 24" fill="currentColor">
            <path fillRule="evenodd" d="M10 2a8 8 0 105.293 14.707l4.387 4.387a1 1 0 001.414-1.414l-4.387-4.387A8 8 0 0010 2zm-6 8a6 6 0 1112 0 6 6 0 01-12 0z" clipRule="evenodd" />
          </svg>
        </div>
        <div>
          <select className="shadow-2xl p-2 border border-black w-full outline-none max-w-4xl">
            <option value="" disabled selected>
              খেলা অনুসন্ধান করুন
            </option>
            <option value="exchange">exchange</option>
          </select>
        </div>
      </div>

      <div className="bg-common-blue mt-4 h-full p-2">
        <h3 className="bg-blue-800 shadow-3xl-blue flex items-center gap-2 p-3 rounded-lg">
          <div className="flex flex-row gap-4">
            <div className="flex gap-2 items-center">
              <input type="checkbox" id="betswiz" className="w-4 h-4 cursor-pointer" />
              <label htmlFor="betswiz" className="text-white font-medium">EVO</label>
            </div>
            <div className="flex gap-2 items-center">
              <input type="checkbox" id="betswiz" className="w-4 h-4 cursor-pointer" />
              <label htmlFor="betswiz" className="text-white font-medium">AP</label>
            </div>
            <div className="flex gap-2 items-center">
              <input type="checkbox" id="betswiz" className="w-4 h-4 cursor-pointer" />
              <label htmlFor="betswiz" className="text-white font-medium">AE</label>
            </div>
          </div>
        </h3>
      </div>

      <div className="grid  grid-cols-1 md:grid-cols-3 lg:grid-cols-5 px-4 flex-wrap pt-3  gap-4 justify-start">
        {currentImages.map((image, index) => (
          <div key={index} className="relative w-full h-auto bg-cover bg-center">
            <figure>
              <div className="bg-black">
                <img src={image.src} alt={image.alt} className="hover:bg-black w-full" />
              </div>
              <button className="absolute inset-10 inset-x-20 flex items-center justify-center text-xs bg-common-orange text-white rounded-lg opacity-0 hover:opacity-100 transition-opacity duration-300">
                এখন খেলুন
              </button>
            </figure>
          </div>
        ))}
      </div>

      <div className="flex justify-center items-center my-4">
        <button
          onClick={() => handlePageChange(currentPage - 1)}
          disabled={currentPage === 1}
          className="px-4 py-2 bg-white text-black font-bold border border-black rounded-md hover:bg-common-blue hover:text-white transition-all disabled:opacity-50"
        >
          Previous
        </button>

        <div className="flex items-center space-x-2 mx-4">
          {Array.from({ length: totalPages }, (_, index) => (
            <button
              key={index + 1}
              onClick={() => handlePageChange(index + 1)}
              className={`px-4 py-2 text-black border border-gray-300 rounded-md 
                ${currentPage === index + 1 ? 'bg-common-blue text-white' : 'bg-white hover:bg-common-blue hover:text-white'} 
                transition-all`}
            >
              {index + 1}
            </button>
          ))}
        </div>

        <button
          onClick={() => handlePageChange(currentPage + 1)}
          disabled={currentPage === totalPages}
          className="px-4 py-2 bg-white font-bold text-black border border-black rounded-md hover:bg-common-blue hover:text-white transition-all disabled:opacity-50"
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default LiveCasino;
