import React from "react";

const DabiVoutcher = () => {
  return (
    <div className="bg-informationBackground min-h-screen flex justify-center items-center">
      <div className="mx-auto max-w-3xl bg-white p-6 shadow-md rounded-md">
        <h3 className="text-lg text-left mb-2">দাবি ভাউচার</h3>
        <h3 className="text-left mb-2">Apply Voucher</h3>
        
        <input
          type="text"
          inputMode="numeric"
          pattern="[0-9]*"
          placeholder="Enter Voucher Code"
          className="border w-full px-4 py-2 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        
        {/* "জমা দিন" button input field er niche niye ashlam */}
        <button className="mt-3 w-full bg-blue-500 text-white py-2 rounded-md hover:bg-blue-600 transition">
          জমা দিন
        </button>
      </div>
    </div>
  );
};

export default DabiVoutcher;
