import React, { useState } from 'react';

const About = () => {
  const [activeTab, setActiveTab] = useState('tab1');

  return (
    <section className="p-8">
      <h2 className="text-xl font-semibold mb-4">About Us</h2>
      <div className="tabs">
        <button
          className={`tab ${activeTab === 'tab1' ? 'tab-active' : ''}`}
          onClick={() => setActiveTab('tab1')}
        >
          Tab 1
        </button>
        <button
          className={`tab ${activeTab === 'tab2' ? 'tab-active' : ''}`}
          onClick={() => setActiveTab('tab2')}
        >
          Tab 2
        </button>
        <button
          className={`tab ${activeTab === 'tab3' ? 'tab-active' : ''}`}
          onClick={() => setActiveTab('tab3')}
        >
          Tab 3
        </button>
      </div>
      <div className="tab-content mt-4">
        {activeTab === 'tab1' && <p>Content for Tab 1</p>}
        {activeTab === 'tab2' && <p>Content for Tab 2</p>}
        {activeTab === 'tab3' && <p>Content for Tab 3</p>}
      </div>
    </section>
  );
};

export default About;
