import { useState } from "react";
import bkashlogo from '../../assets/bkashlogo.svg';
import nagadlogo from '../../assets/nagadlogo.svg';
import rocketlogo from '../../assets/rocketlogo.svg';
import upailogo from '../../assets/upaylogo.svg';
import bajilogo from '../../assets/bajipay.jpeg';

const Amanot = () => {
  const [activeTab, setActiveTab] = useState("deposit");
  const [selectedMethod, setSelectedMethod] = useState(null);
  const [selectedAmount, setSelectedAmount] = useState("");

  const paymentMethods = [
    { name: "bKash", logo: bkashlogo },
    { name: "NOGOD", logo: nagadlogo },
    { name: "Rocket", logo: rocketlogo },
    { name: "Upai", logo: upailogo },
  ];

  const channelsData = {
    bKash: [{ name: "Bajipay", logo: bajilogo }],
    NOGOD: [{ name: "Speedpay" }],
    Rocket: [{ name: "Rocket Channel" }],
    Upai: [{ name: "Upai Service" }],
  };

  const amounts = [500, 1000, 1500, 2000, 5000];

  return (
    <div>
      <div className="bg-common-blue ">
        <div className="flex justify-center w-full mx-auto max-w-lg p-6 ">
          <button
            className={`w-full px-4 py-2 text-sm text-white font-semibold z-10 ${
              activeTab === "deposit" ? "bg-common-orange " : "bg-uttoloncolor "
            } `}
            onClick={() => setActiveTab("deposit")}
          >
            আমানত
          </button>
          <button
            className={`w-full px-4 py-2 text-sm text-white font-semibold z-10 ${
              activeTab === "withdraw" ? "bg-common-orange " : "bg-uttoloncolor "
            } `}
            onClick={() => setActiveTab("withdraw")}
          >
            উত্তোলন
          </button>
        </div>
      </div>

      <div className="max-w-3xl mx-auto p-6 rounded-lg shadow-lg">
        {activeTab === "deposit" && (
          <div className="p-6 rounded-b-md shadow-lg">
            <h3 className="text-lg font-semibold mb-3">মুল্য পরিশোধ পদ্ধতি</h3>
            <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 mb-6">
              {paymentMethods.map((method) => (
                <div
                  key={method.name}
                  className={`card bg-white shadow-md p-4 rounded-lg cursor-pointer ${
                    selectedMethod === method.name ? "border-2 border-orange-500" : ""
                  }`}
                  onClick={() => setSelectedMethod(method.name)}
                >
                  <img src={method.logo} alt={method.name} className="h-12 mx-auto mb-3" />
                  <h4 className="text-center font-semibold">{method.name}</h4>
                </div>
              ))}
            </div>

            {selectedMethod && (
              <>
                <h3 className="text-lg font-semibold mb-3">আমানত চ্যানেল</h3>
                <div className="grid grid-cols-2 gap-4 mb-6 bg-amanotcardcolor">
                  {channelsData[selectedMethod]?.map((channel, index) => (
                    <div key={index} className="card bg-amanotcardcolor shadow-md p-4 rounded-lg">
                      {channel.logo && <img src={channel.logo} alt={channel.name} className="h-12 mx-auto mb-3" />}
                      <h4 className="text-center font-semibold">{channel.name}</h4>
                    </div>
                  ))}
                </div>
              </>
            )}

            <h3 className="text-lg font-semibold mb-3">পরিমাণ</h3>
            <div className="grid grid-cols-2 sm:grid-cols-3 gap-4 mb-6">
              {amounts.map((amount) => (
                <div
                  key={amount}
                  className={`card bg-white shadow-md p-4 rounded-lg cursor-pointer ${
                    selectedAmount === amount ? "border-2 border-orange-500" : ""
                  }`}
                  onClick={() => setSelectedAmount(amount)}
                >
                  <h4 className="text-center font-semibold">৳ {amount}</h4>
                </div>
              ))}
            </div>

            <div className="mb-6">
              <label className="block text-sm font-medium">পরিমাণ (৳)</label>
              <input
                type="text"
                value={selectedAmount || ""}
                readOnly
                className="w-full px-3 py-2 border rounded-md"
              />
            </div>

            <button className="w-full bg-common-orange text-white py-2 rounded-md">জমা দিন</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Amanot;
