import React, { useState } from 'react';

const About = () => {
  const [activeTab, setActiveTab] = useState(null); // No tab is active initially

  return (
    <section className="p-8 relative">
      <h2 className="text-xl font-semibold mb-4">আমাদের সম্পর্কে</h2>
      <div className="flex space-x-4 mb-4">
        {/* Tab 1 */}
        <button
          className={`py-2 px-4 rounded-lg transition duration-300 ${
            activeTab === 'tab1' ? 'bg-blue-500 text-white' : 'bg-gray-200'
          }`}
          onClick={() => setActiveTab(activeTab === 'tab1' ? null : 'tab1')}
        >
          ট্যাব ১
        </button>

        {/* Tab 2 */}
        <button
          className={`py-2 px-4 rounded-lg transition duration-300 ${
            activeTab === 'tab2' ? 'bg-blue-500 text-white' : 'bg-gray-200'
          }`}
          onClick={() => setActiveTab(activeTab === 'tab2' ? null : 'tab2')}
        >
          ট্যাব ২
        </button>

        {/* Tab 3 */}
        <button
          className={`py-2 px-4 rounded-lg transition duration-300 ${
            activeTab === 'tab3' ? 'bg-blue-500 text-white' : 'bg-gray-200'
          }`}
          onClick={() => setActiveTab(activeTab === 'tab3' ? null : 'tab3')}
        >
          ট্যাব ৩
        </button>
      </div>

      {/* Tab Content */}
      <div className="tab-content mt-4 relative">
        {activeTab === 'tab1' && (
          <div className="absolute top-0 left-0 w-full">
            <img
              src="https://via.placeholder.com/300"
              alt="Tab 1 Example"
              className="mb-4 w-full h-auto"
            />
            <p>এইটি ট্যাব ১ এর বিষয়বস্তু। এটি প্রথম বিষয়ের জন্য তথ্য সরবরাহ করে।</p>
          </div>
        )}
        {activeTab === 'tab2' && (
          <div className="absolute top-0 left-0 w-full">
            <img
              src="https://via.placeholder.com/300"
              alt="Tab 2 Example"
              className="mb-4 w-full h-auto"
            />
            <p>এইটি ট্যাব ২ এর বিষয়বস্তু। এটি দ্বিতীয় বিষয়ের জন্য তথ্য সরবরাহ করে।</p>
          </div>
        )}
        {activeTab === 'tab3' && (
          <div className="absolute top-0 left-0 w-full">
            <img
              src="https://via.placeholder.com/300"
              alt="Tab 3 Example"
              className="mb-4 w-full h-auto"
            />
            <p>এইটি ট্যাব ৩ এর বিষয়বস্তু। এটি তৃতীয় বিষয়ের জন্য তথ্য সরবরাহ করে।</p>
          </div>
        )}
      </div>
    </section>
  );
};

export default About;
