import React from "react";
import slotgamesimage from "../../assets/slotgames.jpg";
import ttlog1 from '../../assets/ttlogo1.jpg';
import ttlog2 from '../../assets/ttlogo2.jpg';
import ttlog3 from '../../assets/ttlogo3.jpg';
import ttlog4 from '../../assets/ttlogo4.jpg';
import ttlog5 from '../../assets/ttlogo5.jpg';
import ttlog6 from '../../assets/ttlogo7 (2).png';
import ttlog7 from '../../assets/ttlogo7 (2).png';
import ttlog8 from '../../assets/ttlogo8.png';
import ttlog9 from '../../assets/ttlogo9.jpg';
import ttlog10 from '../../assets/ttlogo10.png';
import ttlog11 from '../../assets/ttlogo11.png';
import ttlog12 from '../../assets/ttlogo12.png';
import ttlog13 from '../../assets/ttlogo13.png';
import ttlog14 from '../../assets/ttlogo14.jpg';
import ttlog15 from '../../assets/ttlogo15.png';
import ttlog16 from '../../assets/ttlogo16.png';
import ttlog17 from '../../assets/ttlogo17.jpg';
import ttlog18 from '../../assets/lllogo18 (2).webp';
import ttlog19 from '../../assets/ttlogo19.jpg';
import ttlog20 from '../../assets/lllogo20.webp';

import hoticon from "../../assets/game-icon-hot-mobile.svg";
import newicon from "../../assets/game-icon-new-mobile.svg";

const images = [
  { src: ttlog1, alt: 'Image 1', title: 'Title 1' },
  { src: ttlog2, alt: 'Image 2', title: 'Title 2' },
  { src: ttlog3, alt: 'Image 3', title: 'Title 3' },
  { src: ttlog4, alt: 'Image 4', title: 'Title 4' },
  { src: ttlog5, alt: 'Image 5', title: 'Title 5' },
  { src: ttlog6, alt: 'Image 6', title: 'Title 6' },
  { src: ttlog7, alt: 'Image 7', title: 'Title 7' },
  { src: ttlog8, alt: 'Image 8', title: 'Title 8' },
  { src: ttlog9, alt: 'Image 9', title: 'Title 9' },
  { src: ttlog10, alt: 'Image 10', title: 'Title 10' },
  { src: ttlog11, alt: 'Image 11', title: 'Title 11' },
  { src: ttlog12, alt: 'Image 12', title: 'Title 12' },
  { src: ttlog13, alt: 'Image 13', title: 'Title 13' },
  { src: ttlog14, alt: 'Image 14', title: 'Title 14' },
  { src: ttlog15, alt: 'Image 15', title: 'Title 15' },
  { src: ttlog16, alt: 'Image 16', title: 'Title 16' },
  { src: ttlog17, alt: 'Image 17', title: 'Title 17' },
  { src: ttlog18, alt: 'Image 18', title: 'Title 18' },
  { src: ttlog19, alt: 'Image 19', title: 'Title 19' },
  { src: ttlog20, alt: 'Image 20', title: 'Title 20' }
];

const itemsPerPage = 20; // Number of images per page
const totalPages = Math.ceil(images.length / itemsPerPage);

const SlotGames = () => {
  const [currentPage, setCurrentPage] = useState(1);
  const indexOfLastImage = currentPage * itemsPerPage;
  const indexOfFirstImage = indexOfLastImage - itemsPerPage;
  const currentImages = images.slice(indexOfFirstImage, indexOfLastImage);

  const handlePageChange = (page) => {
    if (page >= 1 && page <= totalPages) {
      setCurrentPage(page);
    }
  };
  return (
    <div>
      <img src={slotgamesimage} alt="" />
      <div className="bg-common-blue mt-4 h-full p-2">
        <h3 className="bg-blue-800 shadow-3xl-blue flex items-center gap-2 p-3 rounded-lg">
          <div className="flex flex-row gap-4">
            <div className="flex gap-2 items-center">
              <input
                type="checkbox"
                id="betswiz"
                className="w-4 h-4 cursor-pointer"
              />
              <label htmlFor="betswiz" className="text-white font-medium">
                SPRIBE
              </label>
            </div>
            <div className="flex gap-2 items-center">
              <input
                type="checkbox"
                id="betswiz"
                className="w-4 h-4 cursor-pointer"
              />
              <label htmlFor="betswiz" className="text-white font-medium">
                JILI
              </label>
            </div>
            <div className="flex gap-2 items-center">
              <input
                type="checkbox"
                id="betswiz"
                className="w-4 h-4 cursor-pointer"
              />
              <label htmlFor="betswiz" className="text-white font-medium">
                TITO
              </label>
            </div>
            <div className="flex gap-2 items-center">
              <input
                type="checkbox"
                id="betswiz"
                className="w-4 h-4 cursor-pointer"
              />
              <label htmlFor="betswiz" className="text-white font-medium">
                KM
              </label>
            </div>
            <div className="flex gap-2 items-center">
              <input
                type="checkbox"
                id="betswiz"
                className="w-4 h-4 cursor-pointer"
              />
              <label htmlFor="betswiz" className="text-white font-medium">
                RT
              </label>
            </div>
          </div>
        </h3>
      </div>
      <div className="grid  grid-cols-1 md:grid-cols-3 lg:grid-cols-5 px-4 flex-wrap pt-3  gap-4 justify-start">
        {currentImages.map((image, index) => (
          <div
            key={index}
            className="relative w-full h-auto bg-cover bg-center group"
          >
            <figure>
              <div className="relative w-fit">
                <img
                  src={image.src}
                  alt={image.alt}
                  className="hover:bg-black w-full"
                />
                <div className="absolute inset-0 bg-black opacity-0 group-hover:opacity-50 transition duration-300"></div>
              </div>
              <button className="absolute inset-y-16 inset-x-20 whitespace-nowrap flex items-center justify-center text-xs bg-common-orange text-white rounded-sm opacity-0 hover:opacity-100 transition-opacity duration-300">
                এখন খেলুন
              </button>
              <h3 className="absolute  bottom-0 left-0 bg-exchange-color text-white font-bold w-full shadow-exchange-shadow  text-center ">
                {image.title}
              </h3>
              {index < 10 ? (
                <div className="absolute top-0 right-0 flex items-center justify-center text-xs rounded-lg p-2 transition-opacity duration-300">
                  <img src={hoticon} alt="Hot Icon" />
                </div>
              ) : (
                <div className="absolute top-0 right-0 flex items-center justify-center text-xs rounded-lg p-2 transition-opacity duration-300">
                  <img src={newicon} alt="New Icon" />
                </div>
              )}
            </figure>
          </div>
        ))}
      </div>
      


    </div>
  );
};

export default SlotGames;
