import { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

const SidebarTabs = () => {
  const [activeTab, setActiveTab] = useState("tab1");
  const location = useLocation();

  useEffect(() => {
    const tabFromHash = location.hash.replace("#", "");
    if (tabFromHash) {
      setActiveTab(tabFromHash);
    }
    document.getElementById("tab-content")?.scrollIntoView({ behavior: "smooth" });
  }, [location]);

  const tabs = [
    { id: "tab1", label: "Tab 1", content: <p>
        This is content for Tab 1. Here you can add text, images, or any other elements.</p> },
    { id: "tab2", label: "Tab 2", content: <ul><li>Item 1</li><li>Item 2</li><li>Item 3</li></ul> },
    { id: "tab3", label: "Tab 3", content: <img src="https://via.placeholder.com/150" alt="Placeholder" /> },
    { id: "tab4", label: "Tab 4", content: <form><input type="text" placeholder="Enter something..." className="p-2 border rounded" /></form> },
  ];

  return (
    <div className="flex h-screen">
      {/* Sidebar Tabs */}
      <div className="w-1/4 bg-gray-800 text-white flex flex-col p-4 space-y-2">
        {tabs.map((tab) => (
          <a
            key={tab.id}
            href={`#${tab.id}`}
            className={`p-3 text-left rounded-lg transition-all duration-300 block ${
              activeTab === tab.id ? "bg-gray-600" : "hover:bg-gray-700"
            }`}
            onClick={() => setActiveTab(tab.id)}
          >
            {tab.label}
          </a>
        ))}
      </div>

      {/* Content Area */}
      <div id="tab-content" className="w-3/4 p-6 bg-gray-100 flex items-center justify-center text-lg font-semibold">
        {tabs.find((tab) => tab.id === activeTab)?.content}
      </div>
    </div>
  );
};

export default SidebarTabs;
