import { useState, useEffect } from "react";
import bkashlogo from "../../assets/bkashlogo.svg";
import nagadlogo from "../../assets/nagadlogo.svg";
import rocketlogo from "../../assets/rocketlogo.svg";
import upailogo from "../../assets/upaylogo.svg";
import bajilogo from "../../assets/bajipay.jpeg";

const Amanot = () => {
  const [activeTab, setActiveTab] = useState("deposit");
  const [selectedMethod, setSelectedMethod] = useState("bKash");
  const [selectedChannel, setSelectedChannel] = useState({
    name: "Bajipay",
    amounts: [500, 1000, 2000],
  });
  const [selectedAmount, setSelectedAmount] = useState(500); // Default to the first amount in the selected channel
  const [selectedBonus, setSelectedBonus] = useState("");

  const paymentMethods = [
    { name: "bKash", logo: bkashlogo },
    { name: "NOGOD", logo: nagadlogo },
    { name: "Rocket", logo: rocketlogo },
    { name: "Upai", logo: upailogo },
  ];

  const channelsData = {
    bKash: [
      { name: "Bajipay", logo: bajilogo, amounts: [200, 500, 1000, 2000,10000,20000] },
      { name: "Speedpay", amounts: [200 ,500, 1000, 2000, 10000,20000] },
    ],
    NOGOD: [
      { name: "Paybangla", amounts: [500, 1000, 2000, 10000,20000] },
      { name: "Bajipay", amounts: [200, 500, 1000, 2000, 10000, 20000] },
      { name: "Speedpay", amounts: [200, 500, 1000, 2000, 10000, 20000] },
    ],
    Rocket: [
      { name: "Bajipay", amounts: [200, 500, 1000, 2000, 10000, 20000] },
    ],
    Upai: [
      { name: "Fastpay", amounts: [200, 500, 1000, 2000, 10000, 20000] },
    ],
  };

  const bonusOptions = [0, 100, 200, 500]; // Example bonus options

  // Set the default selected channel and amount when selectedMethod changes
  useEffect(() => {
    const defaultChannel = channelsData[selectedMethod]?.[0] || {};
    setSelectedChannel(defaultChannel);
    setSelectedAmount(defaultChannel.amounts ? defaultChannel.amounts[0] : "");
  }, [selectedMethod]);

  return (
    <div>
      <div className="bg-common-blue">
        <div className="flex justify-center w-full mx-auto max-w-3xl p-4">
          <button
            className={`w-full px-4 py-2 text-sm text-white font-semibold ${
              activeTab === "deposit" ? "bg-common-orange" : "bg-uttoloncolor"
            }`}
            onClick={() => setActiveTab("deposit")}
          >
            আমানত
          </button>
          <button
            className={`w-full px-4 py-2 text-sm text-white font-semibold ${
              activeTab === "withdraw" ? "bg-common-orange" : "bg-uttoloncolor"
            }`}
            onClick={() => setActiveTab("withdraw")}
          >
            উত্তোলন
          </button>
        </div>
      </div>

      <div className="max-w-3xl mx-auto  rounded-lg shadow-lg">
        {activeTab === "deposit" && (
          <div className="p-4 rounded-b-md shadow-lg">
            <div className="bg-amanotcardcolor p-3">
            <h3 className="text-lg font-semibold mb-3 py-2 border-b border-black">মূল্য পরিশোধ পদ্ধতি</h3>
            <div className="grid grid-cols-2 lg:grid-cols-6 gap-3  mb-4">
              {paymentMethods.map((method) => (
                <div
                  key={method.name}
                  className={` bg-gray-200 shadow-md p-1 rounded-lg cursor-pointer ${
                    selectedMethod === method.name ? "border-2 border-orange-500" : ""
                  }`}
                  onClick={() => {
                    setSelectedMethod(method.name);
                    setSelectedChannel(null);
                    setSelectedAmount(""); // Reset the amount on method change
                    setSelectedBonus(""); // Reset bonus when changing method
                  }}
                >
                  <img
                    src={method.logo}
                    alt={method.name}
                    className="h-6 mx-auto mb-2"
                  />
                  <h4 className="text-center text-md font-semibold">{method.name}</h4>
                </div>
              ))}
            </div>
            </div>

            {selectedMethod && (
              <>
               <div className="bg-amanotcardcolor p-3 mt-2">
               <h3 className="text-lg font-semibold mb-3 py-2 border-b border-black">আমানত চ্যানেল</h3>
                <div className="grid lg:grid-cols-6 gap-3 mb-4 items-center bg-amanotcardcolor">
                  {channelsData[selectedMethod]?.map((channel, index) => (
                    <div
                      key={index}
                      className={` bg-gray-200 shadow-md p-2 rounded-lg cursor-pointer ${
                        selectedChannel?.name === channel.name ? "border-2 border-orange-500" : ""
                      }`}
                      onClick={() => {
                        setSelectedChannel(channel);
                        setSelectedAmount(channel.amounts[0]); // Default first amount
                      }}
                    >
                     <div className="flex flex-row items-center justify-center">
                     {channel.logo && (
                        <img
                          src={channel.logo}
                          alt={channel.name}
                          className="h-6 mx-auto "
                        />
                      )}
                      <h4 className="text-center text-md  font-semibold">{channel.name}</h4>
                     </div>
                    </div>
                  ))}
                </div>
               </div>
              </>
            )}

            {selectedChannel && (
              <>
               <div className="bg-amanotcardcolor p-3 mt-2">
               <h3 className="text-lg font-semibold mb-3 py-2 border-b border-black">পরিমাণ</h3>
                <div className="grid grid-cols-2 lg:grid-cols-6 gap-3 mb-4">
                  {selectedChannel.amounts.map((amount) => (
                    <div
                      key={amount}
                      className={`bg-gray-200 shadow-md p-2 rounded-lg cursor-pointer ${
                        selectedAmount === amount ? "border-2 border-orange-500" : ""
                      }`}
                      onClick={() => setSelectedAmount(amount)}
                    >
                      <h4 className="text-center text-sm font-semibold">৳ {amount}</h4>
                    </div>
                  ))}
                </div>
               </div>
                <div className="mb-4 p-3 border-b border-black bg-amanotcardcolor flex flex-row items-center ">
                  <label className=" text-2xl mb-2 h-auto text-green-700 font-medium"> ৳</label>
                  <input
                    type="number"
                    value={selectedAmount || ""}
                    readOnly
                    className="w-full px-1 py-1 bg-amanotcardcolor  rounded-md text-sm"
                  />
                </div>

                {/* Bonus Select Field */}
                <div className="mb-4 bg-amanotcardcolor mt-2">
                  <label className="block text-lg font-semibold mb-3 py-2 border-b border-black">আমানত বোনাস</label>
                  <select
                    value={selectedBonus}
                    onChange={(e) => setSelectedBonus(e.target.value)}
                    className="w-1/3 h-full px-3 py-1 border-2 border-black   ro text-lg font-semibold"
                  >
                    <option value="" >No Bonus</option>
                    {bonusOptions.map((bonus) => (
                      <option key={bonus} value={bonus}>
                        ৳ {bonus}
                      </option>
                    ))}
                  </select>
                </div>
              </>
            )}

            <button
              className="w-full bg-common-blue text-white py-2 rounded-md"
              disabled={!selectedChannel || !selectedAmount}
            >
              আমানত
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Amanot;
