import React, { useState } from 'react';

const About = () => {
  const [activeTab, setActiveTab] = useState(null); // Initialize to null to start with no tab active

  return (
    <section className="p-8">
      <h2 className="text-xl font-semibold mb-4">About Us</h2>
      <div className="flex space-x-4 mb-4">
        <button
          className={`py-2 px-4 rounded-lg transition duration-300 ${
            activeTab === 'tab1' ? 'bg-blue-500 text-white' : 'bg-gray-200'
          }`}
          onClick={() => setActiveTab(activeTab === 'tab1' ? null : 'tab1')} // Toggle visibility
        >
          Tab 1
        </button>
        <button
          className={`py-2 px-4 rounded-lg transition duration-300 ${
            activeTab === 'tab2' ? 'bg-blue-500 text-white' : 'bg-gray-200'
          }`}
          onClick={() => setActiveTab(activeTab === 'tab2' ? null : 'tab2')} // Toggle visibility
        >
          Tab 2
        </button>
        <button
          className={`py-2 px-4 rounded-lg transition duration-300 ${
            activeTab === 'tab3' ? 'bg-blue-500 text-white' : 'bg-gray-200'
          }`}
          onClick={() => setActiveTab(activeTab === 'tab3' ? null : 'tab3')} // Toggle visibility
        >
          Tab 3
        </button>
      </div>
      <div className="tab-content">
        {activeTab === 'tab1' && <p>Content for Tab 1</p>}
        {activeTab === 'tab2' && <p>Content for Tab 2</p>}
        {activeTab === 'tab3' && <p>Content for Tab 3</p>}
      </div>
    </section>
  );
};

export default About;
