import { useState } from "react";
import bkashlogo from "../../assets/bkashlogo.svg";
import nagadlogo from "../../assets/nagadlogo.svg";
import rocketlogo from "../../assets/rocketlogo.svg";
import upailogo from "../../assets/upaylogo.svg";
import bajilogo from "../../assets/bajipay.jpeg";

const Amanot = () => {
  const [activeTab, setActiveTab] = useState("deposit");
  const [selectedMethod, setSelectedMethod] = useState("bKash");
  const [selectedAmount, setSelectedAmount] = useState("");

  // পেমেন্ট মেথড আলাদা ভাবে সংরক্ষণ
  const depositMethods = [
    { name: "bKash", logo: bkashlogo },
    { name: "NOGOD", logo: nagadlogo },
    { name: "Rocket", logo: rocketlogo },
    { name: "Upai", logo: upailogo },
  ];

  const withdrawMethods = [
    { name: "bKash", logo: bkashlogo },
    { name: "NOGOD", logo: nagadlogo },
    { name: "Rocket", logo: rocketlogo },
  ];

  // চ্যানেল ডাটা আলাদা ভাবে সংরক্ষণ
  const depositChannels = {
    bKash: [{ name: "Bajipay", logo: bajilogo }],
    NOGOD: [{ name: "Speedpay" }],
    Rocket: [{ name: "Rocket Channel" }],
    Upai: [{ name: "Upai Service" }],
  };

  const withdrawChannels = {
    bKash: [{ name: "Bkash Withdraw" }],
    NOGOD: [{ name: "Nagad Withdraw" }],
    Rocket: [{ name: "Rocket Withdraw" }],
  };

  // পরিমাণ আলাদা ভাবে সংরক্ষণ
  const depositAmounts = [500, 1000, 1500, 2000, 5000];
  const withdrawAmounts = [1000, 2000, 3000, 5000, 10000];

  // ডাটা পরিবর্তনের জন্য ফাংশন
  const paymentMethods = activeTab === "deposit" ? depositMethods : withdrawMethods;
  const channelsData = activeTab === "deposit" ? depositChannels : withdrawChannels;
  const amounts = activeTab === "deposit" ? depositAmounts : withdrawAmounts;

  return (
    <div>
      {/* ট্যাব সুইচিং */}
      <div className="bg-common-blue">
        <div className="flex justify-center w-full mx-auto max-w-lg p-4">
          <button
            className={`w-full px-4 py-2 text-sm text-white font-semibold ${
              activeTab === "deposit" ? "bg-common-orange" : "bg-uttoloncolor"
            }`}
            onClick={() => {
              setActiveTab("deposit");
              setSelectedMethod("bKash");
              setSelectedAmount("");
            }}
          >
            আমানত
          </button>
          <button
            className={`w-full px-4 py-2 text-sm text-white font-semibold ${
              activeTab === "withdraw" ? "bg-common-orange" : "bg-uttoloncolor"
            }`}
            onClick={() => {
              setActiveTab("withdraw");
              setSelectedMethod("bKash");
              setSelectedAmount("");
            }}
          >
            উত্তোলন
          </button>
        </div>
      </div>

      {/* মূল কনটেন্ট */}
      <div className="max-w-3xl mx-auto p-4 rounded-lg shadow-lg">
        <div className="p-4 rounded-b-md shadow-lg">
          <h3 className="text-lg font-semibold mb-3">
            {activeTab === "deposit" ? "মূল্য পরিশোধ পদ্ধতি" : "উত্তোলন পদ্ধতি"}
          </h3>

          {/* পেমেন্ট মেথড নির্বাচন */}
          <div className="grid grid-cols-2 sm:grid-cols-4 gap-3 mb-4">
            {paymentMethods.map((method) => (
              <div
                key={method.name}
                className={`card bg-white shadow-md p-1 rounded-lg cursor-pointer ${
                  selectedMethod === method.name ? "border-2 border-orange-500" : ""
                }`}
                onClick={() => setSelectedMethod(method.name)}
              >
                <img src={method.logo} alt={method.name} className="h-10 mx-auto mb-2" />
                <h4 className="text-center text-sm font-semibold">{method.name}</h4>
              </div>
            ))}
          </div>

          {/* চ্যানেল নির্বাচন */}
          {selectedMethod && (
            <>
              <h3 className="text-lg font-semibold mb-3">
                {activeTab === "deposit" ? "আমানত চ্যানেল" : "উত্তোলন চ্যানেল"}
              </h3>
              <div className="grid grid-cols-2 gap-3 mb-4 bg-amanotcardcolor">
                {channelsData[selectedMethod]?.map((channel, index) => (
                  <div key={index} className="card bg-amanotcardcolor shadow-md p-2 rounded-lg">
                    {channel.logo && (
                      <img src={channel.logo} alt={channel.name} className="h-10 mx-auto mb-2" />
                    )}
                    <h4 className="text-center text-sm font-semibold">{channel.name}</h4>
                  </div>
                ))}
              </div>
            </>
          )}

          {/* পরিমাণ নির্বাচন */}
          <h3 className="text-lg font-semibold mb-3">পরিমাণ</h3>
          <div className="grid grid-cols-2 sm:grid-cols-3 gap-3 mb-4">
            {amounts.map((amount) => (
              <div
                key={amount}
                className={`card bg-white shadow-md p-2 rounded-lg cursor-pointer ${
                  selectedAmount === amount ? "border-2 border-orange-500" : ""
                }`}
                onClick={() => setSelectedAmount(amount)}
              >
                <h4 className="text-center text-sm font-semibold">৳ {amount}</h4>
              </div>
            ))}
          </div>

          {/* ইনপুট */}
          <div className="mb-4">
            <label className="block text-sm font-medium">পরিমাণ (৳)</label>
            <input
              type="text"
              value={selectedAmount || ""}
              readOnly
              className="w-full px-3 py-1 border rounded-md text-sm"
            />
          </div>

          {/* সাবমিট বাটন */}
          <button className="w-full bg-common-orange text-white py-2 rounded-md">
            {activeTab === "deposit" ? "জমা দিন" : "উত্তোলন করুন"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Amanot;
