import { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

const SidebarTabs = () => {
  const [activeTab, setActiveTab] = useState("tab1");
  const location = useLocation();

  useEffect(() => {
    const tabFromHash = location.hash.replace("#", "");
    if (tabFromHash) {
      setActiveTab(tabFromHash);
    }
    document
      .getElementById("tab-content")
      ?.scrollIntoView({ behavior: "smooth" });
  }, [location]);

  const tabs = [
    {
      id: "tab1",
      label: "প্রায়শই জিজ্ঞাসিত প্রশ্নাবলী",
      content: (
        <p>
          প্রায়শই জিজ্ঞাসিত প্রশ্নাবলী বয়সের প্রয়োজনীয়তা JAYA9-এর গ্রাহকদের
          অবশ্যই 18 বছর বা তার বেশি বয়সী হতে হবে এবং কোম্পানির (প্রতিষ্ঠানের)
          দ্বারা নির্ধারিত শর্তাবলী মেনে চলতে সম্মত হতে হবে। আপনার ব্যবহারকারী
          নাম বা পাসওয়ার্ড ভুলে গেছেন? গ্রাহকরা স্বয়ংক্রিয়ভাবে 'পাসওয়ার্ড
          ভুলে গেছেন' ক্লিক করে অনলাইন গ্রাহক পরিষেবার সাথে লিঙ্ক করতে পারেন।
          ২৪-ঘন্টা অনলাইন গ্রাহক পরিষেবা পেশাদার যাচাইকরণ পরিকল্পনা পাস করার পরে
          আপনাকে একটি সমাধান প্রদান করবে। কিভাবে জমা করতে হয় প্ল্যাটফর্মে
          (মঞ্চে) জমা করার বিভিন্ন উপায় রয়েছে (E-Wallet) জমা করার পদক্ষেপ 1.
          প্ল্যাটফর্ম আমানত পৃষ্ঠায় প্রবেশ করতে হোমপেজের উপরের ডানদিকে 'আমানত'
          এ ক্লিক করুন। 2. আপনি যে পরিমাণ টাকা জমা করতে চান তা প্রবেশ করার পর
          ডানদিকে 'চ্যানেল নির্বাচন করুন' ক্লিক করুন। 3. আপনি ডিপোজিট চ্যানেল
          নিচে ক্লিক করতে পারেন। (রিচার্জের উপরের এবং নিচের সীমা পেমেন্ট
          চ্যানেলের সাপেক্ষে) কিভাবে টাকা তোলা যায় হোম-পৃষ্ঠার উপরের ডানদিকে
          "প্রত্যাহার করুন" এ ক্লিক করুন, আপনার তোলার পাসওয়ার্ড এবং আপনি যে
          পরিমাণ প্রত্যাহার করতে চান তা লিখুন, তারপর আপনার কাজ শেষ হলে
          "প্রত্যাহার করুন" এ ক্লিক করুন৷ প্রথম প্রত্যাহারের জন্য, প্রত্যাহার
          ব্যাঙ্ক কার্ডটি ব্যাঙ্ক কার্ড বার্তার সাথে আবদ্ধ করুন। প্রতি লেনদেন
          প্রত্যাহারের সীমা হল ৳800 এবং প্রতিদিন তোলার সীমা হল ৳30,000৷
          ব্যক্তিগত তথ্য নিরাপত্তা কোম্পানি গ্যারান্টি দেয় যে আপনার ব্যক্তিগত
          তথ্য তৃতীয় পক্ষের সাথে শেয়ার করা হবে না। এছাড়াও আমরা আপনার
          ব্যক্তিগত তথ্যের নিরাপত্তার নিশ্চয়তা দিই এবং আপনার তথ্য শুধুমাত্র
          আমাদের কাছে সীমাবদ্ধ করি। কেন্দ্রীয় ওয়ালেট সমস্ত আমানত এবং বোনাস
          একটি একক কেন্দ্রীয় ওয়ালেটে (মানিব্যাগ) জমা করা হয় এবং আপনি বাজি
          রাখতে পারেন।{" "}
        </p>
      ),
    },
    {
      id: "tab2",
      label: "আমাদের সম্পর্কে",
      content: (
        <ul>
          <li>Item 1</li>
          <li>Item 2</li>
          <li>Item 3</li>
        </ul>
      ),
    },
    {
      id: "tab3",
      label: "যোগাযোগ করুন",
      content: <img src="https://via.placeholder.com/150" alt="Placeholder" />,
    },
    {
      id: "tab4",
      label: "শর্তাবলী",
      content: (
        <form>
          <input
            type="text"
            placeholder="Enter something..."
            className="p-2 border rounded"
          />
        </form>
      ),
    },
  ];

  return (
    <div className="bg-informationBackground">
      <div className="flex mx-auto max-w-6xl h-screen">
        {/* Sidebar Tabs */}
        <div className="w-1/4 bg-white text-black flex flex-col p-4 space-y-2">
          {tabs.map((tab) => (
            
            <a
              key={tab.id}
              href={`#${tab.id}`}
              className={`p-3 text-left rounded-lg transition-all duration-300 block ${
                activeTab === tab.id ? "bg-informationBackground" : "hover:bg-informationBackground"
              }`}
              onClick={() => setActiveTab(tab.id)}
            >
              {tab.label}
            </a>
          ))}
        </div>

        {/* Content Area */}
        <div
          id="tab-content"
          className="w-3/4 p-6 ml-4 bg-gray-100 flex items-center justify-center text-lg font-semibold"
        >
          {tabs.find((tab) => tab.id === activeTab)?.content}
        </div>
      </div>
    </div>
  );
};

export default SidebarTabs;
