import React, { useState } from "react";
import infologo from "../../assets/personal-info.jpg";
import memberlogo from "../../assets/memberpic.svg";

const Jachaikoron = () => {
  const [isEditOpen, setIsEditOpen] = useState(false);
  const [isAddNumberOpen, setIsAddNumberOpen] = useState(false);
  const [isOtpOpen, setIsOtpOpen] = useState(false);
  const [otp, setOtp] = useState('');
  const [generatedOtp, setGeneratedOtp] = useState(generateOtp());

  const handleChangeOtp = (e) => {
    setOtp(e.target.value);
  };
  const handleReload = () => {
    setGeneratedOtp(generateOt());
    setOtp(''); // Optionally reset the input field
  };

  const [memberInfo, setMemberInfo] = useState({
    username: "রোহান",
    email: "rakibuhasanrakib@gmail.com",
    dob: "1995-05-12",
    mobile: "+880 ******8002",
    currency: "BDT",
    fullname: " রোহান  চৌধুরী",
  });

  const [formData, setFormData] = useState(memberInfo);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    setMemberInfo(formData); // মূল স্টেট আপডেট হবে
    setIsEditOpen(false);
    console.log(formData); // মডাল বন্ধ হবে
  };
  return (
    <div className="mx-auto max-w-2xl lg:max-w-3xl px-4 lg:px-0 min-h-screen mt-12 relative ">
      <div className="relative">
        <img
          src={infologo}
          alt=""
          className="max-h-28 w-full md:max-h-32 rounded-2xl lg:max-h-32 "
        />
        <img
          src={memberlogo}
          alt=""
          className="w-20 h-auto absolute top-20 left-1/2 transform -translate-x-1/2"
        />
      </div>

      <div>
        <div className="lg:mt-12 bg-memberProfileColor mt-20  pt-8 shadow-lg rounded-lg ">
          {/* Member Information */}
          <div>
            <div className="flex justify-between border-b border-black p-3 items-center mb-4">
              <div className="flex flex-col ">
                <h2 className="text-xl font-semibold">ব্যবহারকারীর নাম</h2>
                <p className="text-memberinfotextcolor font-bold">
                  {" "}
                  {memberInfo.username}
                </p>
              </div>
              <button
                onClick={() => setIsEditOpen(true)}
                className="text-memberinfotextcolor font-bold hover:underline"
              >
                Edit Profile
              </button>
            </div>

            {/* Mobile Number */}
            <div className="border-b border-black p-3">
              <h3 className="text-lg font-semibold">মোবাইল নম্বর</h3>
              <div className="flex justify-between items-center">
                <p className="text-memberinfotextcolor font-bold">
                  {memberInfo.mobile}
                </p>
                <button
                  onClick={() => setIsOtpOpen(true)}
                  className="text-memberinfotextcolor font-bold hover:underline"
                >
                  Verify OTP
                </button>
              </div>
              <button
                onClick={() => setIsAddNumberOpen(true)}
                className="mt-2 text-blue-500 hover:underline block"
              >
                + Add Secondary Number
              </button>
            </div>

            {/* Email & Other Info */}
            <div className="mt-4">
              <p className="flex flex-col p-3 border-b  border-black">
                <strong>ইমেইল:</strong>{" "}
                <span className="text-memberinfotextcolor font-bold">
                  {memberInfo.email}
                </span>
              </p>
              <p className="flex flex-col border-b border-black p-3">
                <strong>জন্ম তারিখ:</strong>{" "}
                <span
                  className="text-memberinfotextcolor
          font-bold"
                >
                  {memberInfo.dob}
                </span>
              </p>
              <p className="flex flex-col border-b border-black p-3">
                <strong>মুদ্রা:</strong>{" "}
                <span className="text-memberinfotextcolor font-bold">BDT</span>
              </p>
            </div>
          </div>
          {/* Edit Profile Modal */}
          {isEditOpen && (
            <div
              className="fixed inset-0 z-50  bg-gray-800 bg-opacity-50 flex justify-center items-center "
              onClick={(e) => {
                if (e.target === e.currentTarget) {
                  setIsEditOpen(false); // Modal বন্ধ হবে যদি বাহিরের জায়গায় ক্লিক করা হয়
                }
              }}
            >
              <div className="bg-white h-auto  lg:w-96 ">
                <div className="px-3 py-6 text-white font-semibold bg-common-blue flex flex-row items-center">
                  <h2 className="text-lg w-full ">Edit Profile</h2>
                  <button
                    onClick={() => setIsEditOpen(false)}
                    className="mt-2 text-gray-500 l"
                  >
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="24"
                      height="24"
                      viewBox="0 0 24 24"
                      fill="none"
                      stroke="white"
                      stroke-width="2"
                      stroke-linecap="round"
                      stroke-linejoin="round"
                    >
                      <line x1="18" y1="6" x2="6" y2="18" />
                      <line x1="6" y1="6" x2="18" y2="18" />
                    </svg>
                  </button>
                </div>
                <form
                  onSubmit={handleSubmit}
                  className=" p-4 space-y-4  rounded-lg"
                >
                  <div>
                    <label className="block font-medium">ইমেইল</label>
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      className="w-full p-2 border border-gray-500 rounded"
                    />
                  </div>
                  <div>
                    <label className="block font-medium">
                      ব্যবহারকারীর নাম
                    </label>
                    <input
                      type="text"
                      name="username"
                      value={formData.username}
                      onChange={handleChange}
                      className="w-full p-2 order border border-gray-500 rounded"
                    />
                  </div>
                  <div>
                    <label className="block font-medium">জন্ম তারিখ</label>
                    <input
                      type="date"
                      name="dob"
                      value={formData.dob}
                      onChange={handleChange}
                      className="w-full p-2 order border border-gray-500 rounded"
                    />
                  </div>

                  <div>
                    <label className="block font-medium">মোবাইল নম্বর</label>
                    <input
                      type="tel"
                      name="mobile"
                      value={formData.mobile}
                      onChange={handleChange}
                      className="w-full p-2 order border border-gray-500 rounded"
                    />
                  </div>

                  <div>
                    <label className="block font-medium">পুরো নাম</label>
                    <input
                      type="text"
                      name="username"
                      value={formData.fullname}
                      onChange={handleChange}
                      className="w-full p-2 order border border-gray-500 rounded"
                    />
                  </div>

                  {/* <div>
                    <label className="block font-medium">Verify OTP</label>
                    <input
                      type="text"
                      name="otp"
                      value={formData.otp}
                      onChange={handleChange}
                      className="w-full p-2 border rounded"
                    />
                  </div> */}

                  {/* <div>
                    <label className="block font-medium">মুদ্রা</label>
                    <select
                      name="currency"
                      value={formData.currency}
                      onChange={handleChange}
                      className="w-full p-2 border rounded"
                    >
                      <option value="BDT">BDT</option>
                      <option value="USD">USD</option>
                      <option value="EUR">EUR</option>
                    </select>
                  </div> */}
                  <button
                    type="submit"
                    onClick={handleSubmit}
                    className="bg-common-blue whitespace-nowrap text-white px-4 py-2 rounded-lg lg:w-1/3 mt-2"
                  >
                    জমা দিন
                  </button>
                </form>
              </div>
            </div>
          )}

          {/* Add Secondary Number Modal */}
          {isAddNumberOpen && (
            <div className="fixed inset-0 z-50  bg-gray-800 bg-opacity-50 flex justify-center items-center">
              <div className="bg-white  rounded-lg w-96 shadow-lg">
                <h2 className="text-lg p-3 bg-common-blue text-white font-semibold mb-4">
                  Verify Phone Number
                </h2>
                <div>
                  <label className="block font-medium">ভেরিফিকেশন কোড</label>
                 <div>
                 <input
                    type="tel" // You can use 'tel' for numeric input
                    name="otp"
                    value={formData.otp}
                    onChange={handleChange}
                    maxLength="6" // Assuming OTP is 6 digits
                    className="w-full p-2 border border-gray-500 rounded"
                  />
                 </div>
                </div>

                <input
                  type="text"
                  placeholder="Enter Number"
                  className="w-full p-2 border rounded mb-2"
                />
                <button className="bg-blue-500 text-white px-4 py-2 rounded w-full mt-2">
                  Add Number
                </button>
                <button
                  onClick={() => setIsAddNumberOpen(false)}
                  className="mt-2 text-gray-500"
                >
                  Cancel
                </button>
              </div>
            </div>
          )}

          {/* Verify OTP Modal */}
          {isOtpOpen && (
            <div className="absolute inset-0 bg-gray-800 bg-opacity-50 flex justify-center items-center">
              <div className="bg-white p-6 rounded-lg w-96 shadow-lg">
                <h2 className="text-lg font-semibold mb-4">Verify OTP</h2>
                <input
                  type="text"
                  placeholder="Enter OTP"
                  className="w-full p-2 border rounded mb-2"
                />
                <button className="bg-green-500 text-white px-4 py-2 rounded w-full mt-2">
                  Verify
                </button>
                <button
                  onClick={() => setIsOtpOpen(false)}
                  className="mt-2 text-gray-500"
                >
                  Cancel
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Jachaikoron;
