import { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

const SidebarTabs = () => {
  const [activeTab, setActiveTab] = useState("tab1");
  const location = useLocation();

  useEffect(() => {
    const tabFromHash = location.hash.replace("#", "");
    if (tabFromHash) {
      setActiveTab(tabFromHash);
    }
    document.getElementById("tab-content")?.scrollIntoView({ behavior: "smooth" });
  }, [location]);

  const tabs = [
    {
      id: "tab1",
      label: "প্রায়শই জিজ্ঞাসিত প্রশ্নাবলী",
      content: (
        <div className="space-y-4">
          <h2 className="text-2xl text-center border-b border-common-blue font-semibold text-co">প্রায়শই জিজ্ঞাসিত প্রশ্নাবলী</h2>
          <p className="text-black border-b border-black leading-relaxed">
          বয়সের প্রয়োজনীয়তা
          </p>
          <p className="text-gray-700 leading-relaxed">
          JAYA9-এর গ্রাহকদের অবশ্যই 18 বছর বা তার বেশি বয়সী হতে হবে এবং কোম্পানির (প্রতিষ্ঠানের) দ্বারা নির্ধারিত শর্তাবলী মেনে চলতে সম্মত হতে হবে।
          </p>
        </div>
      ),
    },
    {
      id: "tab2",
      label: "আমাদের সম্পর্কে",
      content: (
        <div className="space-y-4">
          <h2 className="text-2xl font-semibold text-gray-800">আমাদের সম্পর্কে</h2>
          <p className="text-gray-700 leading-relaxed">
            JAYA9 হল একটি অনলাইন বিনোদন ব্র্যান্ড এবং ক্রিকেট এক্সচেঞ্জ
            প্ল্যাটফর্মের একজন প্রধান বিশেষজ্ঞ।
          </p>
        </div>
      ),
    },
    {
      id: "tab3",
      label: "যোগাযোগ করুন",
      content: (
        <div className="space-y-4">
          <h2 className="text-2xl font-semibold text-gray-800">যোগাযোগ করুন</h2>
          <p className="text-gray-700">গ্রাহক সেবা: CS@brand.com</p>
          <p className="text-gray-700">মার্কেটিং: Marketing@brand.com</p>
          <p className="text-gray-700">অধিভুক্ত: Affiliate@brand.com</p>
        </div>
      ),
    },
    {
      id: "tab4",
      label: "শর্তাবলী",
      content: (
        <div className="space-y-4">
          <h2 className="text-2xl font-semibold text-gray-800">শর্তাবলী</h2>
          <p className="text-gray-700">আপনার অ্যাকাউন্ট ব্যবহারের নিয়মাবলী এবং শর্তাবলী জানতে আমাদের ওয়েবসাইট দেখুন।</p>
        </div>
      ),
    },
  ];

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-4">
      {/* Mobile Tabs */}
      

      {/* Main Layout */}
      <div className="flex flex-col md:flex-row max-w-6xl w-full bg-white shadow-lg rounded-lg overflow-hidden">
        {/* Sidebar */}
        <div className="w-full md:w-1/4 bg-white text-black flex flex-col p-6 space-y-3 shadow-md md:flex">
          <h3 className="text-center text-xl font-semibold border-b-4 border-blue-600 pb-2">অন্যান্য বিষয়</h3>
          {tabs.map((tab) => (
            <button
              key={tab.id}
              className={`p-3 text-left rounded-lg transition-all duration-300 font-medium block ${
                activeTab === tab.id ? "bg-blue-600 text-white" : "hover:bg-gray-200"
              }`}
              onClick={() => setActiveTab(tab.id)}
            >
              {tab.label}
            </button>
          ))}
        </div>

        {/* Content */}
        <div id="tab-content" className="w-full md:w-3/4 p-6">
          {tabs.find((tab) => tab.id === activeTab)?.content}
        </div>
      </div>
    </div>
  );
};

export default SidebarTabs;
