import React from 'react';
import { useEffect, useState } from "react";


const Home = () => {
    const images = [
        "https://img.daisyui.com/images/stock/photo-1625726411847-8cbb60cc71e6.webp",
        "https://img.daisyui.com/images/stock/photo-1609621838510-5ad474b7d25d.webp",
        "https://img.daisyui.com/images/stock/photo-1414694762283-acccc27bca85.webp",
        "https://img.daisyui.com/images/stock/photo-1665553365602-b2fb8e5d1707.webp",
      ];
      const [currentIndex, setCurrentIndex] = useState(0);

      useEffect(() => {
        const interval = setInterval(() => {
          setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
        }, 3000); // 3 seconds interval
    
        return () => clearInterval(interval);
      }, [images.length]);

    return (
        <div>
            <div className="w-full flex flex-col items-center">
      {/* Image Container */}
      <div className="relative w-full lg:grid lg:grid-cols-1 overflow-hidden">
        <img
          src={images[currentIndex]}
          alt="carousel image"
          className="w-full h-64 object-cover transition-opacity duration-500"
        />

        {/* Navigation Buttons Inside Image */}
        <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex gap-2 bg-black bg-opacity-50 px-4 py-2 rounded-lg">
          {images.map((_, index) => (
            <button
              key={index}
              onClick={() => setCurrentIndex(index)}
              className={`w-3 h-3 rounded-full ${
                currentIndex === index ? "bg-white" : "bg-gray-400"
              }`}
            ></button>
          ))}
        </div>
      </div>
    </div>
        </div>
    );
};

export default Home;
