import React from "react";
import { useState } from "react";
import nibondonimage from "../../assets/bd-desktop-679a25600aae8.jpg";

const Nibondon = () => {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    confirmPassword: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Log the data to console for confirmation
    console.log("Form Data:", formData);
  };
  return (
    <div className="pl-8 pr-72 ">
        <div className="flex flex-row gap-2 border-4 border-common-blue p-8">
      {/* Form Section */}
      <form onSubmit={handleSubmit} className="w-1/3 space-y-4">
        <div>
          <h3 className="text-common-orange text-center border-b border-common-orange">নিবন্ধন</h3>
        </div>
        <div>
          <h3 className="text-black">ব্যবহারকারীর নাম</h3>
          <input
            type="text"
            name="username"
            placeholder="এখানে পূরণ করুন"
            className="w-full p-2 border rounded-md focus:outline-none focus:ring-1 focus:ring-red-500"
            value={formData.username}
            onChange={handleChange}
          />
        </div>
        <div>
          <h3 className="text-black">গোপন নম্বর</h3>
          <input
            type="password"
            name="password"
            placeholder="এখানে পূরণ করুন"
            className="w-full p-2 border rounded-md focus:outline-none focus:ring-1 focus:ring-red-500"
            value={formData.password}
            onChange={handleChange}
          />
        </div>
        <div>
          <h3 className="text-black">পাসওয়ার্ড নিশ্চিত করুন</h3>
          <input
            type="password"
            name="confirmPassword"
            placeholder="এখানে পূরণ করুন"
            className="w-full p-2 border rounded-md focus:outline-none focus:ring-1 focus:ring-red-500"
            value={formData.confirmPassword}
            onChange={handleChange}
          />
        </div>
        <div>
          <button
            type="submit"
            className="w-full bg-orange-500 text-white py-2 rounded-md hover:bg-orange-600"
          >
            প্রবেশ করুন
          </button>
        </div>
      </form>

      {/* Image Section */}
      <div className="w-1/">
        <img
          src={nibondonimage} // You can replace this with your actual image URL
          alt=""
          className="w-full h-full object-cover cursor-pointer"
          onClick={() => console.log("Image clicked")}
        />
      </div>
    </div>
    </div>
  );
};

export default Nibondon;
