import { useState } from "react";

const Amanot = () => {
  const [activeTab, setActiveTab] = useState("deposit");

  const paymentMethods = [
    { name: "bKash", logo: "/path/to/bkash-logo.png" },
    { name: "NOGOD", logo: "/path/to/nogod-logo.png" },
    { name: "Rocket", logo: "/path/to/rocket-logo.png" },
    { name: "Upai", logo: "/path/to/upai-logo.png" },
  ];

  const channels = [
    { name: "Bajipay", logo: "/path/to/bajipay-logo.png" },
    { name: "Speedpay", logo: "/path/to/speedpay-logo.png" },
  ];

  const amounts = [
    { amount: 500 },
    { amount: 1000 },
    { amount: 1500 },
    { amount: 2000 },
    { amount: 5000 },
  ];

  return (
    <div className="bg-common-blue min-h-screen">
      <div className="flex justify-center space-x-4 p-6">
        <button
          className={`w-full sm:w-auto px-4 py-2 text-sm text-white font-semibold ${
            activeTab === "deposit" ? "bg-common-orange" : "bg-uttoloncolor"
          }`}
          onClick={() => setActiveTab("deposit")}
        >
          à¦à¦®à¦¾à¦¨à¦¤
        </button>
        <button
          className={`w-full sm:w-auto px-4 py-2 text-sm text-white font-semibold ${
            activeTab === "withdraw" ? "bg-common-orange" : "bg-uttoloncolor"
          }`}
          onClick={() => setActiveTab("withdraw")}
        >
          à¦à¦¤à§à¦¤à§à¦²à¦¨
        </button>
      </div>

      <div className="max-w-full sm:max-w-lg mx-auto p-6 rounded-lg shadow-lg bg-white">
        {/* Deposit Form */}
        {activeTab === "deposit" && (
          <div>
            {/* Payment Method Section */}
            <h3 className="text-lg font-semibold mb-3">à¦®à§à¦²à§à¦¯ à¦ªà¦°à¦¿à¦¶à§à¦§ à¦ªà¦¦à§à¦§à¦¤à¦¿</h3>
            <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 mb-6">
              {paymentMethods.map((method, index) => (
                <div key={index} className="bg-white shadow-md p-4 rounded-lg">
                  <img
                    src={method.logo}
                    alt={method.name}
                    className="h-12 mx-auto mb-3"
                  />
                  <h4 className="text-center font-semibold">{method.name}</h4>
                </div>
              ))}
            </div>

            {/* Channels Section */}
            <h3 className="text-lg font-semibold mb-3">à¦à¦®à¦¾à¦¨à¦¤ à¦à§à¦¯à¦¾à¦¨à§à¦²</h3>
            <div className="grid grid-cols-2 gap-4 mb-6">
              {channels.map((channel, index) => (
                <div key={index} className="bg-white shadow-md p-4 rounded-lg">
                  <img
                    src={channel.logo}
                    alt={channel.name}
                    className="h-12 mx-auto mb-3"
                  />
                  <h4 className="text-center font-semibold">{channel.name}</h4>
                </div>
              ))}
            </div>

            {/* Amount Section */}
            <h3 className="text-lg font-semibold mb-3">à¦ªà¦°à¦¿à¦®à¦¾à¦£</h3>
            <div className="grid grid-cols-2 sm:grid-cols-3 gap-4 mb-6">
              {amounts.map((item, index) => (
                <div key={index} className="bg-white shadow-md p-4 rounded-lg">
                  <h4 className="text-center font-semibold">{`à§³ ${item.amount}`}</h4>
                </div>
              ))}
            </div>

            {/* Amanot Bonus Section */}
            <h3 className="text-lg font-semibold mb-3">à¦à¦®à¦¾à¦¨à¦¤ à¦¬à§à¦¨à¦¾à¦¸</h3>
            <div className="mb-6">
              <label htmlFor="bonus" className="block mb-2 text-sm">
                à¦¬à§à¦¨à¦¾à¦¸ à¦¨à¦¿à¦°à§à¦¬à¦¾à¦à¦¨ à¦à¦°à§à¦¨
              </label>
              <select id="bonus" className="block w-full p-2 rounded-lg">
                <option>No Bonus</option>
                <option>Bonus 1</option>
                <option>Bonus 2</option>
              </select>
            </div>

            <button className="w-full bg-common-orange text-white py-2 rounded-md">
              à¦à¦®à¦¾ à¦¦à¦¿à¦¨
            </button>
          </div>
        )}

        {/* Withdraw Form */}
        {activeTab === "withdraw" && (
          <div>
            <h3 className="text-lg font-semibold mb-3">à¦ªà§à¦°à¦§à¦¾à¦¨ à¦à¦¯à¦¼à¦¾à¦²à§à¦</h3>
            <div className="bg-gray-100 p-3 rounded-md mb-2">à§³ 0.00</div>
            <p className="text-red-500 text-sm">
              Phone number is not verified.
            </p>
            <button className="text-blue-600 underline mb-2">à¦¯à¦¾à¦à¦¾à¦ à¦à¦°à§à¦¨</button>

            <h3 className="text-lg font-semibold mb-3">à¦®à§à¦²à§à¦¯ à¦ªà¦°à¦¿à¦¶à§à¦§ à¦ªà¦¦à§à¦§à¦¤à¦¿</h3>
            <div className="mb-2">
              <label className="block text-sm font-medium">à¦ªà¦°à¦¿à¦®à¦¾à¦£ (à§³)</label>
              <input
                type="number"
                placeholder="à¦¨à§à¦¯à§à¦¨à¦¤à¦® à§³ 500.00 - à¦¸à¦°à§à¦¬à§à¦à§à¦ à§³ 30,000.00"
                className="w-full px-3 py-2 border rounded-md"
              />
            </div>
            <div className="mb-3">
              <label className="block text-sm font-medium">à¦®à§à¦¬à¦¾à¦à¦² à¦¨à¦®à§à¦¬à¦°</label>
              <input
                type="text"
                defaultValue="+8801939228002"
                className="w-full px-3 py-2 border rounded-md"
              />
            </div>
            <button className="w-full bg-common-orange text-white py-2 rounded-md">
              à¦à¦¤à§à¦¤à§à¦²à¦¨ à¦à¦°à§à¦¨
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Amanot;
