import React, { useState } from 'react';

const About = () => {
  const [activeTab, setActiveTab] = useState(null); // Initialize to null to start with no tab active

  return (
    <section className="p-8">
      <h2 className="text-xl font-semibold mb-4">About Us</h2>
      <div className="flex space-x-4 mb-4">
        <button
          className={`py-2 px-4 rounded-lg transition duration-300 ${
            activeTab === 'tab1' ? 'bg-blue-500 text-white' : 'bg-gray-200'
          }`}
          onClick={() => setActiveTab(activeTab === 'tab1' ? null : 'tab1')} // Toggle visibility
        >
          Our Mission
        </button>
        <button
          className={`py-2 px-4 rounded-lg transition duration-300 ${
            activeTab === 'tab2' ? 'bg-blue-500 text-white' : 'bg-gray-200'
          }`}
          onClick={() => setActiveTab(activeTab === 'tab2' ? null : 'tab2')} // Toggle visibility
        >
          Our Team
        </button>
        <button
          className={`py-2 px-4 rounded-lg transition duration-300 ${
            activeTab === 'tab3' ? 'bg-blue-500 text-white' : 'bg-gray-200'
          }`}
          onClick={() => setActiveTab(activeTab === 'tab3' ? null : 'tab3')} // Toggle visibility
        >
          Our Values
        </button>
      </div>
      <div className="tab-content">
        {activeTab === 'tab1' && (
          <p>
            Our mission is to provide high-quality services and solutions to our clients, helping them achieve their goals through innovation and expertise. We believe in fostering long-term relationships and ensuring customer satisfaction.
          </p>
        )}
        {activeTab === 'tab2' && (
          <p>
            Our team is composed of experienced professionals from various backgrounds, each bringing unique skills and perspectives to the table. We value collaboration and creativity, striving to create an inclusive and dynamic work environment.
          </p>
        )}
        {activeTab === 'tab3' && (
          <p>
            We are committed to integrity, excellence, and continuous improvement. Our values guide our decision-making processes and shape our culture, ensuring we operate with transparency and respect for all stakeholders.
          </p>
        )}
      </div>
    </section>
  );
};

export default About;
