import lllogo16 from "../../assets/lllogo16.webp";
import lllogo17 from "../../assets/lllogo17 (2).webp";
import lllogo18 from "../../assets/lllogo18 (2).webp";
import lllogo19 from "../../assets/lllogo19.webp";
import lllogo20 from "../../assets/lllogo20.webp";
import lllogo21 from "../../assets/lllog021.webp";
import lllogo22 from "../../assets/lllogo22.webp";
import lllogo23 from "../../assets/lllogo23.webp";
import lllogo24 from "../../assets/lllogo24.webp";
import lllogo25 from "../../assets/lllogo25.webp";
import lllogo26 from "../../assets/lllogo26.webp";
import lllogo27 from "../../assets/llogo27.webp";
import lllogo28 from "../../assets/lllogo28.webp";
import lllogo29 from "../../assets/lllogo29.webp";
import lllogo30 from "../../assets/lllogo30.webp";
import lllogo31 from "../../assets/lllogo31.webp";
import lllogo32 from "../../assets/lllogo32.webp";
import lllogo33 from "../../assets/lllogo33.webp";
import lllogo34 from "../../assets/lllogo34.webp";
import lllogo35 from "../../assets/lllogo35.webp";
import lllogo36 from "../../assets/lllogo36.webp";
import lllogo37 from "../../assets/llllogo37.webp";
import lllogo38 from "../../assets/lllogo38.webp";
import lllogo39 from "../../assets/lllogo39.webp";
import lllogo40 from "../../assets/lllogo40.webp";
import hoticon from "../../assets/game-icon-hot-mobile.svg";
import newicon from "../../assets/game-icon-new-mobile.svg";

const images = [
  { src: lllogo1, alt: " Image 1", title: "evolution gaming" },
  { src: lllogo2, alt: " Image 2", title: "Live Lobby" },
  { src: lllogo3, alt: " Image 3", title: "Evo funky time" },
  { src: lllogo4, alt: "Image 4" , title: "Evo imperial quest"},
  { src: lllogo5, alt: " Image 5", title: "crazy time (20,000x0" },
 
  { src: lllogo10, alt: " Image 10", title: "" },
  { src: lllogo11, alt: " Image 11", title: "evolution gaming" },
  { src: lllogo12, alt: " Image 12", title: "evolution gaming" },
  { src: lllogo13, alt: " Image 13", title: "evolution gaming" },
  { src: lllogo14, alt: " Image 14", title: "evolution gaming" },
  { src: lllogo15, alt: " Image 15" , title: "evolution gaming"},
  { src: lllogo16, alt: " Image 16", title: "evolution gaming" },
  { src: lllogo17, alt: " Image 17", title: "evolution gaming" },
  { src: lllogo18, alt: " Image 18" , title: "evolution gaming"},
  { src: lllogo19, alt: " Image 19", title: "evolution gaming" },
  { src: lllogo20, alt: "Image 20" , title: "evolution gaming"},
  { src: lllogo21, alt: "Image 21" , title: "evolution gaming"},
  { src: lllogo22, alt: "Image 22", title: "evolution gaming" },
  { src: lllogo23, alt: "Image 23", title: "evolution gaming" },
  { src: lllogo24, alt: "Image 24", title: "evolution gaming" },
  { src: lllogo25, alt: "Image 25", title: "evolution gaming" },
  { src: lllogo26, alt: "Image 26" , title: "evolution gaming"},
  { src: lllogo27, alt: "Image 27", title: "evolution gaming" },
  { src: lllogo28, alt: "Image 28", title: "evolution gaming" },
  { src: lllogo29, alt: "Image 29" , title: "evolution gaming"},
  { src: lllogo30, alt: "Image 30" , title: "evolution gaming"},
  { src: lllogo31, alt: "Image 31" , title: "evolution gaming"},
  { src: lllogo32, alt: "Image 32", title: "evolution gaming" },
  { src: lllogo33, alt: "Image 33", title: "evolution gaming" },
  { src: lllogo34, alt: "Image 35" , title: "evolution gaming"},
  { src: lllogo36, alt: "Image 37", title: "evolution gaming" },
  { src: lllogo38, alt: "Image 38", title: "evolution gaming" },
  { src: lllogo39, alt: "Image 39" , title: "evolution gaming"},
  { src: lllogo40, alt: "Image 40", title: "evolution gaming" },
];
const itemsPerPage = 20; // Number of images per page
const totalPages = Math.ceil(images.length / itemsPerPage);

const LiveCasino = () => {
  const [currentPage, setCurrentPage] = useState(1);

  // Get images for the current page
  const indexOfLastImage = currentPage * itemsPerPage;
  const indexOfFirstImage = indexOfLastImage - itemsPerPage;
  const currentImages = images.slice(indexOfFirstImage, indexOfLastImage);

  const handlePageChange = (page) => {
    if (page >= 1 && page <= totalPages) {
      setCurrentPage(page);
    }
  };

  return (
    <div>
      <div className="w-full min-w-full   bg-cover">
        <img
          src={livecasinoimage}
          alt="Live Casino"
          className="w-full h-full object-cover"
        />
      </div>

      <div className="flex flex-row items-center px-4 pt-4">
        <div>
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-6 w-6"
            viewBox="0 0 24 24"
            fill="currentColor"
          >
            <path
              fillRule="evenodd"
              d="M10 2a8 8 0 105.293 14.707l4.387 4.387a1 1 0 001.414-1.414l-4.387-4.387A8 8 0 0010 2zm-6 8a6 6 0 1112 0 6 6 0 01-12 0z"
              clipRule="evenodd"
            />
          </svg>
        </div>
        <div>
          <select className="shadow-2xl p-2 border border-black w-full outline-none max-w-4xl">
            <option value="" disabled selected>
              খেলা অনুসন্ধান করুন
            </option>
            <option value="exchange">exchange</option>
          </select>
        </div>
      </div>

      <div className="bg-common-blue mt-4 h-full p-2">
        <h3 className="bg-blue-800 shadow-3xl-blue flex items-center gap-2 p-3 rounded-lg">
          <div className="flex flex-row gap-4">
            <div className="flex gap-2 items-center">
              <input
                type="checkbox"
                id="betswiz"
                className="w-4 h-4 cursor-pointer"
              />
              <label htmlFor="betswiz" className="text-white font-medium">
                EVO
              </label>
            </div>
            <div className="flex gap-2 items-center">
              <input
                type="checkbox"
                id="betswiz"
                className="w-4 h-4 cursor-pointer"
              />
              <label htmlFor="betswiz" className="text-white font-medium">
                AP
              </label>
            </div>
            <div className="flex gap-2 items-center">
              <input
                type="checkbox"
                id="betswiz"
                className="w-4 h-4 cursor-pointer"
              />
              <label htmlFor="betswiz" className="text-white font-medium">
                AE
              </label>
            </div>
          </div>
        </h3>
      </div>

      <div className="grid  grid-cols-1 md:grid-cols-3 lg:grid-cols-5 px-4 flex-wrap pt-3  gap-4 justify-start">
        {currentImages.map((image, index) => (
          <div
            key={index}
            className="relative w-full h-auto bg-cover bg-center group"
          >
            <figure>
              <div className="relative w-fit">
                <img
                  src={image.src}
                  alt={image.alt}
                  className="hover:bg-black w-full"
                />
                <div className="absolute inset-0 bg-black opacity-0 group-hover:opacity-50 transition duration-300"></div>
              </div>
              <button className="absolute inset-y-16 inset-x-20 whitespace-nowrap flex items-center justify-center text-xs bg-common-orange text-white rounded-sm opacity-0 hover:opacity-100 transition-opacity duration-300">
                এখন খেলুন
              </button>
              <h3 className="absolute  bottom-0 left-0 bg-exchange-color text-white font-bold w-full shadow-exchange-shadow  text-center ">
                exchange
              </h3>
              {index < 10 ? (
                <button className="absolute top-0 right-0 flex items-center justify-center text-xs   rounded-lg p-2  transition-opacity duration-300">
                  <img src={hoticon} alt="" className="" />
                </button>
              ) : (
                <button className="absolute top-0 right-0 flex items-center justify-center text-xs  rounded-lg p-2  transition-opacity duration-300">
                  <img src={newicon} alt="" />
                </button>
              )}
            </figure>
          </div>
        ))}
      </div>

      <div className="flex justify-center items-center my-4">
        <button
          onClick={() => handlePageChange(currentPage - 1)}
          disabled={currentPage === 1}
          className="px-4 py-2 bg-white text-black font-bold border border-black rounded-md hover:bg-common-blue hover:text-white transition-all disabled:opacity-50"
        >
          Previous
        </button>

        <div className="flex items-center space-x-2 mx-4">
          {Array.from({ length: totalPages }, (_, index) => (
            <button
              key={index + 1}
              onClick={() => handlePageChange(index + 1)}
              className={`px-4 py-2 text-black border border-gray-300 rounded-md 
                ${
                  currentPage === index + 1
                    ? "bg-common-blue text-white"
                    : "bg-white hover:bg-common-blue hover:text-white"
                } 
                transition-all`}
            >
              {index + 1}
            </button>
          ))}
        </div>

        <button
          onClick={() => handlePageChange(currentPage + 1)}
          disabled={currentPage === totalPages}
          className="px-4 py-2 bg-white font-bold text-black border border-black rounded-md hover:bg-common-blue hover:text-white transition-all disabled:opacity-50"
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default LiveCasino;
